import React, { useState, useEffect, Suspense, useRef } from 'react';
import { useTranslation } from 'react-i18next';
import { motion, AnimatePresence } from 'framer-motion';
import {
  SunIcon, MoonIcon, LanguageIcon, BriefcaseIcon, AcademicCapIcon,
  SparklesIcon, CodeBracketIcon, LinkIcon, RocketLaunchIcon,
  ChatBubbleLeftRightIcon, ArrowDownTrayIcon, EnvelopeIcon // Adicionado EnvelopeIcon, CodeBracketIcon pode ser removido se não usado mais no hero
} from '@heroicons/react/24/outline';

// Placeholder for 3D elements or visual flourishes
const FloatingShape = ({ className }) => (
  <div className={`absolute w-12 h-12 md:w-16 md:h-16 rounded-full opacity-10 dark:opacity-20 animate-float ${className}`}></div>
);

function App() {
  const { t, i18n } = useTranslation();
  const [darkMode, setDarkMode] = useState(() => {
    const savedMode = localStorage.getItem('darkMode');
    return savedMode ? JSON.parse(savedMode) : true; // Dark mode is default
  });

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
    localStorage.setItem('darkMode', JSON.stringify(darkMode));
  }, [darkMode]);

  const toggleDarkMode = () => setDarkMode(!darkMode);

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };

  const currentLanguage = i18n.language;

  const githubProfileUrl = "https://github.com/DevJpLg";
  const technipFmcLogoUrl = "/images/technipfmc.svg"; // Ensure this path is correct
  const profilePhotoUrl = "/images/profile.jpg"; // Path to your profile photo
  const emailAddress = "joaopedro.lg@hotmail.com";
  const cvFileUrl = "/Joao_Pedro_Goncalves_CV.pdf"; 
  const linkedinProfileUrl = "https://www.linkedin.com/in/joaopedrolopesgoncalves/"; // Restaurada a definição


  // Placeholder for actual projects from GitHub
  const projectsPlaceholder = [
    { id: 1, title: "GitHub Project 1", description: "Description for project 1 from GitHub.", tech: ["React", "Node.js"], image: "https://via.placeholder.com/300x200/FF00FF/FFFFFF?Text=Project+1", link: "#" },
    { id: 2, title: "GitHub Project 2", description: "Description for project 2 from GitHub.", tech: ["Python", "API"], image: "https://via.placeholder.com/300x200/00A9FF/FFFFFF?Text=Project+2", link: "#" },
  ];


  // State for certificate pagination
  const [currentCertificatePage, setCurrentCertificatePage] = useState(0);
  const certificates = t('certifications.certificates', { returnObjects: true }) || []; // Ensure certificates is an array

  const totalCertificatePages = certificates.length;

  const handlePrevCertificate = () => {
    setCurrentCertificatePage((prevPage) => (prevPage === 0 ? totalCertificatePages - 1 : prevPage - 1));
  };

  const handleNextCertificate = () => {
    setCurrentCertificatePage((prevPage) => (prevPage === totalCertificatePages - 1 ? 0 : prevPage + 1));
  };

  const handleDotClick = (index) => {
    setCurrentCertificatePage(index);
  };

  // State for showing/hiding extra certificates
  const [showAllCertificates, setShowAllCertificates] = useState(false);
  const allCertificatesData = t('certifications.certificates', { returnObjects: true }) || [];
  const initialCertificatesToShow = 4;

  const certificationsSectionRef = useRef(null); // Criar a ref para a seção

  const toggleShowAllCertificates = () => {
    // Se estamos prestes a mostrar menos (ou seja, showAllCertificates é true agora)
    if (showAllCertificates && certificationsSectionRef.current) {
      certificationsSectionRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
    setShowAllCertificates(!showAllCertificates);
  };

  // Variantes para animação dos cards
  const cardVariants = {
    hidden: { opacity: 0, y: 20, scale: 0.95 },
    visible: (i) => ({
      opacity: 1,
      y: 0,
      scale: 1,
      transition: {
        delay: i * 0.05, // Delay escalonado para cada card
        duration: 0.3,
        ease: "easeOut"
      }
    }),
    exit: { 
      opacity: 0, 
      y: -10, 
      scale: 0.95,
      transition: { 
        duration: 0.2,
        ease: "easeIn"
      } 
    }
  };

  // Variantes para animação das categorias e skills
  const sectionVariant = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.5,
        when: "beforeChildren", // Anima filhos depois do pai
        staggerChildren: 0.1 // Delay entre animação de filhos
      }
    }
  };

  const skillItemVariant = {
    hidden: { opacity: 0, scale: 0.8, y: 10 },
    visible: {
      opacity: 1,
      scale: 1,
      y: 0,
      transition: { duration: 0.3, ease: "easeOut" }
    }
  };

  const tagVariants = {
    hidden: { opacity: 0, y: 10, scale: 0.9 },
    visible: (i) => ({
      opacity: 1,
      y: 0,
      scale: 1,
      transition: {
        delay: i * 0.1, // Staggered delay for each tag
        duration: 0.3,
        ease: "easeOut",
      },
    }),
  };

  // Mapeamento de tecnologias para suas cores específicas
  const techColorMap = {
    "JavaScript": { bgColor: "bg-yellow-300", textColor: "text-yellow-800" },
    "CSS": { bgColor: "bg-blue-500", textColor: "text-white" },
    "React": { bgColor: "bg-sky-400", textColor: "text-sky-900" },
    "Python": { bgColor: "bg-green-400", textColor: "text-green-900" }, // Ou bg-blue-400 com text-yellow-300 para o logo do Python
    "VBA": { bgColor: "bg-purple-500", textColor: "text-white" },
    "Oracle DB": { bgColor: "bg-red-600", textColor: "text-white" },
    "MS Power Platform": { bgColor: "bg-indigo-500", textColor: "text-white" }
  };

  // Variantes para animação dos cards de educação
  const educationCardVariant = {
    hidden: { opacity: 0, y: 20, scale: 0.95 },
    visible: (i) => ({
      opacity: 1,
      y: 0,
      scale: 1,
      transition: {
        delay: i * 0.1,
        duration: 0.4,
        ease: "easeOut"
      }
    })
  };

  return (
    <div className="min-h-screen flex flex-col relative overflow-x-hidden bg-light-bg dark:bg-brand-purple-dark text-light-text dark:text-dark-text">
