            <p className="text-lg sm:text-xl md:text-2xl font-semibold mb-5 text-gray-200 dark:text-gray-300 max-w-3xl mx-auto">{t('hero.title')}</p>
            <p className="text-md sm:text-lg md:text-xl max-w-2xl mx-auto mb-8 text-gray-300 dark:text-gray-400">
              {t('hero.subtitle')}
            </p>
            <div className="flex flex-wrap justify-center items-center gap-4">
                <a 
                    href={`mailto:${emailAddress}`} 
                    className="btn-primary inline-block"
                >
                  <EnvelopeIcon className="h-5 w-5 inline-block mr-2" /> {t('hero.contactButtonHero')}
                </a>
                {/* Botão "View My Work" original, agora comentado ou removido
                <a href={githubProfileUrl} target="_blank" rel="noopener noreferrer" className="btn-primary inline-block">
                  <CodeBracketIcon className="h-5 w-5 inline-block mr-2" /> {t('hero.ctaButton')}
                </a>
                */}
                <a 
                    href={cvFileUrl}
                    download
                    className="btn-secondary inline-block"
                >
                    <ArrowDownTrayIcon className="h-5 w-5 inline-block mr-2" /> {t('hero.downloadCV')}
                </a>
            </div>
          </div>
        </section>

        {/* About Me Section (Type 1 - Redesigned) */}
        <section id="about" className={`py-16 md:py-24 bg-gray-50 text-light-text dark:bg-brand-purple dark:text-dark-text overflow-hidden`}>
          <div className="container mx-auto px-6">
            <h2 className="text-3xl md:text-4xl font-bold text-center mb-12 flex items-center justify-center">
              <i className="bi bi-person-circle mr-3 text-3xl md:text-4xl text-accent-magenta"></i> {t('about.title')}
            </h2>
            <div className="flex flex-col md:flex-row items-center md:items-start gap-8 md:gap-12 animate-fadeInUp">
              {/* Tamanho da imagem de perfil aumentado */}
              <div className="w-56 h-56 md:w-64 md:h-64 lg:w-80 lg:h-80 flex-shrink-0 rounded-full overflow-hidden shadow-2xl border-4 border-accent-teal dark:border-accent-blue transform transition-all duration-500 hover:scale-105">
                <img 
                  src={profilePhotoUrl} 
                  alt={t('hero.name')} 
                  className="w-full h-full object-cover"
                />
              </div>
              <div className="text-center md:text-left flex-grow">
                <p className="text-2xl md:text-3xl font-semibold mb-4">{t('about.intro')}</p>
                <p className="mb-4 text-base md:text-lg leading-relaxed text-gray-700 dark:text-gray-300">{t('about.paragraph1')}</p>
                <p className="mb-6 text-base md:text-lg leading-relaxed text-gray-700 dark:text-gray-300">{t('about.paragraph2')}</p>
                
                {/* Key Technology Tags */}
                <div className="mb-6 text-center md:text-left">
                  <div className="flex flex-wrap justify-center md:justify-start gap-2 md:gap-3">
                    {(t('about.keyTechTags', { returnObjects: true }) || []).map((tagObj, index) => {
                      // Pega as cores do nosso mapa, ou usa um fallback se a tag não estiver no mapa
                      const colors = techColorMap[tagObj.name] || { bgColor: "bg-gray-400", textColor: "text-gray-800" };
                      return (
                        <motion.span 
                          key={tagObj.name} 
                          custom={index}
                          variants={tagVariants}
                          initial="hidden"
                          animate="visible"
                          className={`px-4 py-1.5 text-xs font-semibold rounded-lg shadow-md
                                      ${colors.bgColor} ${colors.textColor}
                                      transform transition-all duration-300 hover:scale-110 hover:shadow-lg`}
