                          {tagObj.name}
                        </motion.span>
                      );
                    })}
                  </div>
                </div>

                {/* Location and Connections */}
                <div className="space-y-2 text-sm md:text-base text-gray-600 dark:text-gray-400">
                  <a 
                    href={t('about.locationUrl')} 
                    target="_blank" 
                    rel="noopener noreferrer" 
                    className="flex items-center justify-center md:justify-start hover:text-accent-blue dark:hover:text-accent-teal transition-colors"
                  >
                    <i className="bi bi-geo-alt-fill mr-2 text-accent-teal"></i> {t('about.location')}
                  </a>
                  <a 
                    href={t('about.connectionsUrl')} 
                    target="_blank" 
                    rel="noopener noreferrer" 
                    className="flex items-center justify-center md:justify-start hover:text-accent-blue dark:hover:text-accent-teal transition-colors"
                  >
                    <i className="bi bi-people-fill mr-2 text-accent-teal"></i> {t('about.connections')}
                  </a>
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* Experience Section (Type 2) */}
        <motion.section 
          id="experience" 
          className={`py-16 md:py-24 bg-light-bg text-light-text dark:bg-brand-purple-dark dark:text-dark-text`}
          variants={sectionVariant} // sectionVariant from previous step
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.05 }} // Trigger when a small part is visible
        >
          <div className="container mx-auto px-6">
            <h2 className={`text-3xl md:text-4xl font-bold text-center mb-12 flex items-center justify-center`}>
              <BriefcaseIcon className="h-8 w-8 mr-3 text-accent-blue" /> {t('experience.title')}
            </h2>
            <div className="max-w-3xl mx-auto">
              <div className={`p-6 md:p-8 rounded-xl shadow-xl card-hover-glow bg-white text-light-text dark:bg-brand-purple-light dark:text-dark-text`}>
                <div className="flex items-start sm:items-center mb-2 flex-col sm:flex-row">
                  <img
                    src={technipFmcLogoUrl}
                    alt="TechnipFMC Logo"
                    className="h-10 w-10 sm:h-12 sm:w-12 mr-0 sm:mr-4 mb-2 sm:mb-0 rounded-sm object-contain" 
                  />
                  <div>
                    <h3 className="text-2xl font-bold ">{t('experience.technipfmc.role')}</h3>
                    <p className="text-lg font-semibold text-accent-magenta dark:text-accent-teal ">{t('experience.technipfmc.company')}</p>
                  </div>
                </div>
                <p className="text-sm text-gray-500 dark:text-gray-400 mb-1">{t('experience.technipfmc.duration')}</p>
                <p className="text-sm text-gray-500 dark:text-gray-400 mb-4">{t('experience.technipfmc.location')}</p>
                <p className={`mb-4 leading-relaxed text-gray-700 dark:text-gray-300`}>{t('experience.technipfmc.description')}</p>
                <p className={`text-sm font-semibold text-gray-600 dark:text-gray-200`}>{t('experience.technipfmc.skills')}</p>
              </div>
            </div>
          </div>
        </motion.section>

        {/* Education Section (Redesigned) */}
        <motion.section 
          id="education" 
          className={`py-16 md:py-24 bg-light-bg text-light-text dark:bg-brand-purple-dark dark:text-dark-text`}
          variants={sectionVariant} // Reutilizando sectionVariant se definido, ou crie um novo
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.1 }}
        >
          <div className="container mx-auto px-6">
            <h2 className="text-3xl md:text-4xl font-bold text-center mb-16 flex items-center justify-center">
              <AcademicCapIcon className="h-8 w-8 mr-3 text-accent-blue dark:text-accent-teal" /> {t('education.title')}
            </h2>
            <div className="max-w-4xl mx-auto space-y-10">
              {(t('education.items', { returnObjects: true }) || []).map((edu, index) => (
                <motion.div
                  key={edu.key}
                  custom={index}
                  variants={educationCardVariant}
                  className="flex flex-col md:flex-row items-start p-6 bg-white dark:bg-brand-purple-light rounded-xl shadow-xl card-hover-subtle"
                >
                  <div className="flex-shrink-0 w-20 h-20 md:w-24 md:h-24 mb-4 md:mb-0 md:mr-6 rounded-lg bg-gray-100 dark:bg-slate-700 flex items-center justify-center overflow-hidden">
                    {edu.logo && edu.logo !== "/images/generic_school_logo.svg" ? (
                      <img src={edu.logo} alt={`${edu.institution} logo`} className="max-w-full max-h-full object-contain p-1" />
                    ) : (
                      // Ícone genérico se não houver logo específico ou se for o placeholder
                      <i className="bi bi-building text-4xl md:text-5xl text-gray-400 dark:text-gray-500"></i>
                      // Alternativa com Heroicons: <BuildingLibraryIcon className="w-10 h-10 md:w-12 md:w-12 text-gray-400 dark:text-gray-500" />
                    )}
                  </div>
                  <div className="flex-grow">
                    <h3 className="text-xl md:text-2xl font-bold text-gray-800 dark:text-white mb-1">{edu.institution}</h3>
                    <p className="text-md md:text-lg font-semibold text-accent-magenta dark:text-accent-blue mb-1">{edu.degree}</p>
                    <p className="text-sm text-gray-500 dark:text-gray-400 mb-3">{edu.dates}</p>
                    {edu.skills && edu.skills.length > 0 && (
                      <div>
                        <h4 className="text-sm font-semibold text-gray-700 dark:text-gray-300 mb-1 mt-2">Skills:</h4>
                        <div className="flex flex-wrap gap-2">
                          {edu.skills.map(skill => (
                            <span 
                              key={skill} 
                              className="px-2.5 py-0.5 text-xs font-medium bg-accent-teal/15 text-accent-teal dark:bg-accent-blue/15 dark:text-accent-blue rounded-full"
                            >
                              {skill}
                            </span>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                </motion.div>
              ))}
            </div>
          </div>
        </motion.section>

        {/* Skills Section (Type 2 - Redesigned) */}
        <motion.section 
          id="skills" 
          className={`py-16 md:py-24 bg-light-bg text-light-text dark:bg-brand-purple-dark dark:text-dark-text`}
          variants={sectionVariant} // sectionVariant from previous step
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.05 }} // Trigger when a small part is visible
