                <ChatBubbleLeftRightIcon className="h-8 w-8 mr-3 text-accent-blue" /> {t('contact.title')}
            </h2>
            <p className={`text-lg max-w-xl mx-auto mb-8 ${!darkMode ? 'text-gray-300' : 'text-gray-600'}`}>{t('contact.description')}</p>
            <a href={linkedinProfileUrl} target="_blank" rel="noopener noreferrer" className="btn-primary">
              <LinkIcon className="h-5 w-5 inline-block mr-2" /> {t('contact.linkedin')}
            </a>
          </div>
        </section>

      </main>

      <footer className="py-8 text-center bg-brand-purple-dark text-gray-400">
        <div className="container mx-auto px-6">
          <div className="flex justify-center space-x-6 mb-4">
            <a href={linkedinProfileUrl} target="_blank" rel="noopener noreferrer" aria-label="LinkedIn" className="text-gray-400 hover:text-accent-blue transition-colors">
              <i className="bi bi-linkedin text-2xl"></i>
            </a>
            <a href={githubProfileUrl} target="_blank" rel="noopener noreferrer" aria-label="GitHub" className="text-gray-400 hover:text-accent-blue transition-colors">
              <i className="bi bi-github text-2xl"></i>
            </a>
            <a href={`mailto:${emailAddress}`} aria-label="Email" className="text-gray-400 hover:text-accent-blue transition-colors">
              <i className="bi bi-envelope-fill text-2xl"></i>
            </a>
          </div>
          {/* Alterado para usar t('footer.ownerName') */}
          <p className="text-sm">&copy; {new Date().getFullYear()} {t('footer.ownerName')}. {t('footer.rights')}</p>
        </div>
      </footer>
    </div>
  );
}

export default function SuspendedApp() {
  return (
    <Suspense fallback={<div className="flex justify-center items-center min-h-screen text-xl">Loading translations...</div>}>
      <App />
    </Suspense>
  );
}
