import React, { useState, useEffect, Suspense, useRef } from 'react';
import { useTranslation } from 'react-i18next';
import { motion, AnimatePresence } from 'framer-motion';
import {
  SunIcon, MoonIcon, LanguageIcon, BriefcaseIcon, AcademicCapIcon,
  SparklesIcon, CodeBracketIcon, LinkIcon, RocketLaunchIcon,
  ChatBubbleLeftRightIcon, ArrowDownTrayIcon, EnvelopeIcon
  // Certifique-se de que todos os ícones usados estão importados.
  // BuildingLibraryIcon não foi usado, mas se fosse, precisaria de import.
} from '@heroicons/react/24/outline';

// Placeholder for 3D elements or visual flourishes
const FloatingShape = ({ className }) => (
  <div className={`absolute w-12 h-12 md:w-16 md:h-16 rounded-full opacity-10 dark:opacity-20 animate-float ${className}`}></div>
);

function App() {
  const { t, i18n } = useTranslation();
  const [darkMode, setDarkMode] = useState(() => {
    const savedMode = localStorage.getItem('darkMode');
    return savedMode ? JSON.parse(savedMode) : true;
  });

  const toggleDarkMode = () => {
    setDarkMode(prevMode => {
      const newMode = !prevMode;
      localStorage.setItem('darkMode', JSON.stringify(newMode));
      if (newMode) {
        document.documentElement.classList.add('dark');
      } else {
        document.documentElement.classList.remove('dark');
      }
      return newMode;
    });
  };

  const [currentLanguage, setCurrentLanguage] = useState(i18n.language.split('-')[0]);

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
    setCurrentLanguage(lng);
  };

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
    localStorage.setItem('darkMode', JSON.stringify(darkMode));
  }, [darkMode]);

  useEffect(() => {
    setCurrentLanguage(i18n.language.split('-')[0]);
  }, [i18n.language]);


  const [showAllCertificates, setShowAllCertificates] = useState(false);
  const allCertificatesData = t('certifications.certificates', { returnObjects: true }) || [];
  const initialCertificatesToShow = 4;

  const certificationsSectionRef = useRef(null);

  const toggleShowAllCertificates = () => {
    if (showAllCertificates && certificationsSectionRef.current) {
      certificationsSectionRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
    setShowAllCertificates(!showAllCertificates);
  };

  // --- Framer Motion Variants ---
  const cardVariants = { // Para certificados
    hidden: { opacity: 0, y: 20, scale: 0.95 },
    visible: (i) => ({
      opacity: 1,
      y: 0,
      scale: 1,
      transition: { delay: i * 0.05, duration: 0.3, ease: "easeOut" }
    }),
    exit: { opacity: 0, y: -10, scale: 0.95, transition: { duration: 0.2, ease: "easeIn" } }
  };

  const sectionVariant = { // Para seções inteiras como Skills, Education
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.5, when: "beforeChildren", staggerChildren: 0.1 }
    }
  };

  const skillItemVariant = { // Para cards de skills individuais
    hidden: { opacity: 0, scale: 0.8, y: 10 },
    visible: { opacity: 1, scale: 1, y: 0, transition: { duration: 0.3, ease: "easeOut" } }
  };
  
  // Remover educationCardVariant se não for mais usada em outro lugar ou reverter para uma genérica
  // Se educationCardVariant era usada pela seção Experience, pode mantê-la ou usar cardVariants.
  // Para este exemplo, vou assumir que a seção Education usará uma animação mais simples ou nenhuma específica por card.
  /*
  const educationCardVariant = { // Para cards de educação
    hidden: { opacity: 0, y: 20, scale: 0.95 },
    visible: (i) => ({
      opacity: 1,
      y: 0,
      scale: 1,
      transition: { delay: i * 0.1, duration: 0.4, ease: "easeOut" }
    })
  };
  */

  const tagVariants = { // Para tags no About Me
    hidden: { opacity: 0, y: 10, scale: 0.9 },
    visible: (i) => ({
      opacity: 1,
      y: 0,
      scale: 1,
      transition: { delay: i * 0.1, duration: 0.3, ease: "easeOut" },
    }),
  };
  // --- Fim das Framer Motion Variants ---

  const profilePhotoUrl = "/images/profile.jpg";
  const technipFmcLogoUrl = "/images/technipfmc.svg"; // Restaurar/Definir technipFmcLogoUrl
  const linkedinProfileUrl = "https://www.linkedin.com/in/joaopedrolopesgoncalves/";
  const githubProfileUrl = "https://github.com/DevJpLg";
  const emailAddress = "joaopedro.lg@hotmail.com";
  const cvFileUrl = "/Joao_Pedro_Goncalves_CV.pdf";

  const techColorMap = {
    "JavaScript": { bgColor: "bg-yellow-300", textColor: "text-yellow-800" },
    "CSS": { bgColor: "bg-blue-500", textColor: "text-white" },
    "React": { bgColor: "bg-sky-400", textColor: "text-sky-900" },
    "Python": { bgColor: "bg-green-400", textColor: "text-green-900" },
    "VBA": { bgColor: "bg-purple-500", textColor: "text-white" },
    "Oracle DB": { bgColor: "bg-red-600", textColor: "text-white" },
    "MS Power Platform": { bgColor: "bg-indigo-500", textColor: "text-white" }
  };
  
  // Adicionando uma verificação mais robusta para os itens de educação
  const educationItemsData = t('education.items', { returnObjects: true });
  const educationItems = Array.isArray(educationItemsData) ? educationItemsData : [];

  // Restaurar/Definir projectsPlaceholder
  const projectsPlaceholder = [
    {
      id: 1,
      name: "Project Alpha",
      description: "A full-stack web application for managing tasks and projects.",
      html_url: "#",
      language: "JavaScript",
      stargazers_count: 10,
      forks_count: 2,
      topics: ["react", "nodejs", "express", "mongodb"]
    },
    {
      id: 2,
      name: "Data Analyzer",
      description: "A Python tool for analyzing and visualizing large datasets.",
      html_url: "#",
      language: "Python",
      stargazers_count: 25,
      forks_count: 5,
      topics: ["python", "pandas", "matplotlib", "data-science"]
    },
    // Adicione mais placeholders se necessário
  ];


  // Smooth scroll for navigation links
  const handleNavClick = (e, targetId) => {
    e.preventDefault();
    const element = document.getElementById(targetId);
    if (element) {
      window.scrollTo({
        top: element.offsetTop - 64, // Ajuste para o tamanho do seu cabeçalho
        behavior: 'smooth'
      });
    }
  };

  return (
    <div className="min-h-screen flex flex-col relative overflow-x-hidden bg-light-bg dark:bg-brand-purple-dark text-light-text dark:text-dark-text">
      {/* Floating Shapes - Removido para simplificar, pode ser adicionado de volta se necessário */}
      {/* <FloatingShape className="bg-accent-blue top-1/4 left-5 md:left-10" /> */}
      {/* <FloatingShape className="bg-accent-magenta bottom-1/4 right-5 md:right-10" /> */}
      
      {/* Header / Navbar */}
      <header className="sticky top-0 z-50 shadow-lg bg-brand-purple-light/80 dark:bg-brand-purple-dark/80 backdrop-blur-md">
        <nav className="container mx-auto px-6 py-3 flex justify-between items-center">
          <a href="#hero" onClick={(e) => handleNavClick(e, 'hero')} className="text-2xl font-bold text-accent-teal dark:text-accent-blue">
            {t('hero.name').split(' ')[0]} <span className="text-accent-magenta">{t('hero.name').split(' ').slice(1).join(' ')}</span>
          </a>
          <div className="hidden md:flex space-x-4 items-center">
            {['home', 'about', 'experience', 'education', 'skills', 'projects', 'certifications', 'contact'].map((key) => (
              <a key={key} href={`#${key === 'home' ? 'hero' : key}`} onClick={(e) => handleNavClick(e, key === 'home' ? 'hero' : key)} className="nav-link">{t(`nav.${key}`)}</a>
            ))}
          </div>
          <div className="flex items-center space-x-3">
            {/* Language Switcher */}
            <div className="relative">
              <button onClick={() => changeLanguage(currentLanguage === 'en' ? 'pt' : 'en')} className="btn-icon" aria-label="Change language">
                <LanguageIcon className="h-6 w-6" />
                <span className="ml-1 uppercase text-xs font-semibold">{currentLanguage}</span>
              </button>
            </div>
            {/* Dark Mode Toggle */}
            <button onClick={toggleDarkMode} className="btn-icon" aria-label="Toggle dark mode">
              {darkMode ? <SunIcon className="h-6 w-6" /> : <MoonIcon className="h-6 w-6" />}
            </button>
            {/* Mobile Menu Button (opcional, se você tiver um menu mobile) */}
            {/* <button className="md:hidden btn-icon" aria-label="Open menu"> <Bars3Icon className="h-6 w-6" /> </button> */}
          </div>
        </nav>
      </header>

      <main className="flex-grow">
        {/* Hero Section */}
        <section id="hero" className="relative py-20 md:py-32 text-center bg-gradient-purple-dark dark:bg-gradient-to-br dark:from-brand-purple-dark dark:to-brand-purple flex flex-col items-center justify-center min-h-[calc(100vh-64px)] overflow-hidden px-4">
          <div className="absolute inset-0 opacity-10 dark:opacity-20">
            <div className="absolute top-0 left-0 w-1/2 h-1/2 bg-accent-blue rounded-full filter blur-3xl animate-pulseGlow animation-delay-2000"></div>
            <div className="absolute bottom-0 right-0 w-1/3 h-1/3 bg-accent-magenta rounded-full filter blur-3xl animate-pulseGlow"></div>
          </div>
          <div className="container mx-auto z-10 animate-fadeInUp">
            <h1 className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl font-black mb-3 text-white dark:text-dark-text">
              {t('hero.greeting')} <span className="bg-clip-text text-transparent bg-gradient-accent">{t('hero.name')}</span>
            </h1>
            <p className="text-lg sm:text-xl md:text-2xl font-semibold mb-5 text-gray-200 dark:text-gray-300 max-w-3xl mx-auto">{t('hero.title')}</p>
            <p className="text-md sm:text-lg md:text-xl max-w-2xl mx-auto mb-8 text-gray-300 dark:text-gray-400">
              {t('hero.subtitle')}
            </p>
            <div className="flex flex-wrap justify-center items-center gap-4">
                <a 
                    href={`mailto:${emailAddress}`} 
                    className="btn-primary inline-block"
                >
                  <EnvelopeIcon className="h-5 w-5 inline-block mr-2" /> {t('hero.contactButtonHero')}
                </a>
                {/* Botão "View My Work" original, agora comentado ou removido
                <a href={githubProfileUrl} target="_blank" rel="noopener noreferrer" className="btn-primary inline-block">
                  <CodeBracketIcon className="h-5 w-5 inline-block mr-2" /> {t('hero.ctaButton')}
                </a>
                */}
                <a 
                    href={cvFileUrl}
                    download
                    className="btn-secondary inline-block"
                >
                    <ArrowDownTrayIcon className="h-5 w-5 inline-block mr-2" /> {t('hero.downloadCV')}
                </a>
            </div>
          </div>
        </section>

        {/* About Me Section (Type 1 - Redesigned) */}
        <section id="about" className={`py-16 md:py-24 bg-gray-50 text-light-text dark:bg-brand-purple dark:text-dark-text overflow-hidden`}>
          <div className="container mx-auto px-6">
            <h2 className="text-3xl md:text-4xl font-bold text-center mb-12 flex items-center justify-center">
              <i className="bi bi-person-circle mr-3 text-3xl md:text-4xl text-accent-magenta"></i> {t('about.title')}
            </h2>
            <div className="flex flex-col md:flex-row items-center md:items-start gap-8 md:gap-12 animate-fadeInUp">
              {/* Tamanho da imagem de perfil revertido */}
              <div className="w-48 h-48 md:w-60 md:h-60 lg:w-72 lg:h-72 flex-shrink-0 rounded-full overflow-hidden shadow-2xl border-4 border-accent-teal dark:border-accent-blue transform transition-all duration-500 hover:scale-105">
                <img 
                  src={profilePhotoUrl} 
                  alt={t('hero.name')} 
                  className="w-full h-full object-cover"
                />
              </div>
              <div className="text-center md:text-left flex-grow">
                <p className="text-2xl md:text-3xl font-semibold mb-4">{t('about.intro')}</p>
                <p className="mb-4 text-base md:text-lg leading-relaxed text-gray-700 dark:text-gray-300">{t('about.paragraph1')}</p>
                <p className="mb-6 text-base md:text-lg leading-relaxed text-gray-700 dark:text-gray-300">{t('about.paragraph2')}</p>
                
                {/* Key Technology Tags */}
                <div className="mb-6 text-center md:text-left">
                  <div className="flex flex-wrap justify-center md:justify-start gap-2 md:gap-3">
                    {(t('about.keyTechTags', { returnObjects: true }) || []).map((tagObj, index) => {
                      // Pega as cores do nosso mapa, ou usa um fallback se a tag não estiver no mapa
                      const colors = techColorMap[tagObj.name] || { bgColor: "bg-gray-400", textColor: "text-gray-800" };
                      return (
                        <motion.span 
                          key={tagObj.name} 
                          custom={index}
                          variants={tagVariants}
                          initial="hidden"
                          animate="visible"
                          className={`px-4 py-1.5 text-xs font-semibold rounded-lg shadow-md
                                      ${colors.bgColor} ${colors.textColor}
                                      transform transition-all duration-300 hover:scale-110 hover:shadow-lg`}
                        >
                          {tagObj.name}
                        </motion.span>
                      );
                    })}
                  </div>
                </div>

                {/* Location and Connections */}
                <div className="space-y-2 text-sm md:text-base text-gray-600 dark:text-gray-400">
                  <a 
                    href={t('about.locationUrl')} 
                    target="_blank" 
                    rel="noopener noreferrer" 
                    className="flex items-center justify-center md:justify-start hover:text-accent-blue dark:hover:text-accent-teal transition-colors"
                  >
                    <i className="bi bi-geo-alt-fill mr-2 text-accent-teal"></i> {t('about.location')}
                  </a>
                  <a 
                    href={t('about.connectionsUrl')} 
                    target="_blank" 
                    rel="noopener noreferrer" 
                    className="flex items-center justify-center md:justify-start hover:text-accent-blue dark:hover:text-accent-teal transition-colors"
                  >
                    <i className="bi bi-people-fill mr-2 text-accent-teal"></i> {t('about.connections')}
                  </a>
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* Experience Section (Type 2) */}
        <motion.section 
          id="experience" 
          className={`py-16 md:py-24 bg-gray-50 text-light-text dark:bg-brand-purple dark:text-dark-text`}
          variants={sectionVariant}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.1 }}
        >
          <div className="container mx-auto px-6">
            <h2 className="text-3xl md:text-4xl font-bold text-center mb-12 flex items-center justify-center">
              <BriefcaseIcon className="h-8 w-8 mr-3 text-accent-blue dark:text-accent-teal" /> {t('experience.title')}
            </h2>
            <div className="max-w-3xl mx-auto">
              {(t('experience.jobs', { returnObjects: true }) || []).map((job, index) => (
                <motion.div 
                  key={job.key}
                  custom={index}
                  variants={cardVariants} // Reutilizando variante similar
                  className="mb-8 p-6 bg-white dark:bg-brand-purple-light rounded-xl shadow-lg card-hover-subtle"
                >
                  <div className="flex items-start mb-2">
                    {job.key === 'technipfmc' && (
                      <img src={technipFmcLogoUrl} alt="TechnipFMC Logo" className="h-10 w-10 mr-4 mt-1 dark:bg-slate-200 dark:p-1 dark:rounded-sm" />
                    )}
                    <div>
                      <h3 className="text-xl font-bold text-gray-800 dark:text-white">{job.title}</h3>
                      <p className="text-md font-semibold text-accent-magenta dark:text-accent-blue">{job.company}</p>
                      <p className="text-sm text-gray-500 dark:text-gray-400">{job.dates}</p>
                      <p className="text-sm text-gray-500 dark:text-gray-400">{job.location}</p>
                    </div>
                  </div>
                  <ul className="list-disc list-inside space-y-1 text-sm text-gray-700 dark:text-gray-300 pl-2">
                    {job.responsibilities.map((resp, i) => <li key={i}>{resp}</li>)}
                  </ul>
                </motion.div>
              ))}
            </div>
          </div>
        </motion.section>

        {/* Education Section (Reverted to simpler design) */}
        <motion.section 
          id="education" 
          className={`py-16 md:py-24 bg-light-bg text-light-text dark:bg-brand-purple-dark dark:text-dark-text`} // Ajuste o bg se necessário
          variants={sectionVariant} 
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.1 }}
        >
          <div className="container mx-auto px-6">
            <h2 className="text-3xl md:text-4xl font-bold text-center mb-12 flex items-center justify-center">
              <AcademicCapIcon className="h-8 w-8 mr-3 text-accent-blue dark:text-accent-teal" /> {t('education.title')}
            </h2>
            <div className="max-w-3xl mx-auto space-y-8">
              {educationItems.map((edu, index) => (
                <motion.div 
                  key={edu.institution + index} // Chave simples
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true, amount: 0.3 }}
                  transition={{ duration: 0.5, delay: index * 0.1 }}
                  className="p-6 bg-white dark:bg-brand-purple-light rounded-xl shadow-lg"
                >
                  <h3 className="text-xl font-bold text-gray-800 dark:text-white">{edu.institution}</h3>
                  <p className="text-md font-semibold text-accent-magenta dark:text-accent-blue">{edu.degree}</p>
                  <p className="text-sm text-gray-500 dark:text-gray-400">{edu.dates}</p>
                </motion.div>
              ))}
            </div>
          </div>
        </motion.section>

        {/* Skills Section (Type 2 - Redesigned) */}
        <motion.section 
          id="skills" 
          className={`py-16 md:py-24 bg-light-bg text-light-text dark:bg-brand-purple-dark dark:text-dark-text`}
          variants={sectionVariant} // sectionVariant from previous step
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.05 }} // Trigger when a small part is visible
        >
          <div className="container mx-auto px-6">
            <h2 className={`text-3xl md:text-4xl font-bold text-center mb-12 flex items-center justify-center`}>
                <RocketLaunchIcon className="h-8 w-8 mr-3 text-accent-magenta" /> {t('skills.title')}
            </h2>

            {/* Horizontal Bar Chart Section - REMOVIDO */}
            {/*
            <motion.div 
              className="mb-16 md:mb-20 max-w-3xl mx-auto"
              variants={barChartVariant}
            >
              <h3 className="text-xl md:text-2xl font-semibold mb-8 text-center md:text-left text-gray-700 dark:text-gray-200">
                {t('skills.barChart.title')}
              </h3>
              <div className="space-y-5">
                {(t('skills.barChart.items', { returnObjects: true }) || []).map((skill, index) => (
                  <motion.div 
                    key={skill.name} 
                    className="flex items-center"
                  >
                    <span className="w-32 sm:w-36 text-sm sm:text-base font-medium text-gray-700 dark:text-gray-300 pr-3 text-right flex-shrink-0">
                      {skill.name}
                    </span>
                    <div className="flex-1 bg-gray-300 dark:bg-gray-600 rounded-full h-6 md:h-7 overflow-hidden relative">
                      <motion.div
                        className={`absolute top-0 left-0 h-full rounded-full ${skill.color || 'bg-accent-teal'}`}
                        custom={skill.percentage}
                        variants={barItemVariant}
                        initial="hidden"
                        whileInView="visible"
                        viewport={{ once: true, amount: 0.1 }}
                      >
                        <motion.span 
                          className="absolute right-2 top-1/2 -translate-y-1/2 text-xs sm:text-sm font-bold text-white"
                          variants={percentageTextVariant}
                          initial="hidden"
                          whileInView="visible"
                          viewport={{ once: true, amount: 0.1 }} 
                        >
                          {skill.percentage}%
                        </motion.span>
                      </motion.div>
                    </div>
                  </motion.div>
                ))}
              </div>
            </motion.div>
            */}
            
            {/* Skill Categories Section */}
            <div className="space-y-12 md:space-y-16">
              {(t('skills.categories', { returnObjects: true }) || []).map((category, catIndex) => (
                <motion.div 
                  key={category.name}
                  variants={sectionVariant} // Pode usar a mesma variante ou uma específica para categorias
                >
                  <h3 className="text-2xl md:text-3xl font-semibold mb-8 text-center md:text-left flex items-center justify-center md:justify-start">
                    <i className={`${category.bsIcon} mr-3 text-2xl md:text-3xl text-accent-teal dark:text-accent-blue`}></i>
                    {category.name}
                  </h3>
                  <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 xl:grid-cols-6 gap-4 md:gap-6">
                    {(category.items || []).map((skill, skillIndex) => (
                      <motion.div
                        key={skill.name}
                        className="flex flex-col items-center p-4 bg-white dark:bg-brand-purple-light rounded-lg shadow-lg card-hover-glow text-center"
                        variants={skillItemVariant}
                        // whileInView e viewport aqui também se quiser animação individual ao rolar para cada skill
                        // initial="hidden" // Já herdado do pai se using when: "beforeChildren"
                        // animate="visible"
                      >
                        <img 
                          src={skill.icon} 
                          alt={`${skill.name} icon`} 
                          className="h-12 w-12 md:h-16 md:w-16 mb-3 object-contain filter dark:brightness-0 dark:invert-[75%] dark:sepia-[15%] dark:saturate-[500%] dark:hue-rotate-[180deg]"
                          // O filtro acima é uma tentativa de tornar SVGs escuros visíveis no modo escuro. Ajuste conforme necessário.
                          // Se seus SVGs já são adequados para ambos os modos, remova o filtro.
                        />
                        <p className="text-sm md:text-base font-medium text-gray-700 dark:text-gray-200">{skill.name}</p>
                      </motion.div>
                    ))}
                  </div>
                </motion.div>
              ))}
            </div>
          </div>
        </motion.section>

        {/* Projects Section (Type 2) */}
        <section id="projects" className={`py-16 md:py-24 bg-gray-50 text-light-text dark:bg-brand-purple dark:text-dark-text`}>
          <div className="container mx-auto px-6">
            <h2 className="text-3xl md:text-4xl font-bold text-center mb-12 flex items-center justify-center">
              <CodeBracketIcon className="h-8 w-8 mr-3 text-accent-teal" /> {t('projects.title')}
            </h2>
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
              {/* Usando projectsPlaceholder */}
              {(projectsPlaceholder || []).map((project, index) => ( 
                <motion.div 
                  key={project.id || index} // Adicionado fallback para key
                  custom={index}
                  variants={cardVariants} // Reutilizando cardVariants ou crie um específico
                  initial="hidden"
                  whileInView="visible"
                  viewport={{ once: true, amount: 0.2 }}
                  className="bg-white dark:bg-brand-purple-light p-6 rounded-xl shadow-xl card-hover-glow flex flex-col"
                >
                  <h3 className="text-xl font-bold mb-2 text-gray-800 dark:text-white">{project.name}</h3>
                  <p className="text-sm text-gray-700 dark:text-gray-300 mb-3">{project.description}</p>
                  {project.language && (
                    <p className="text-xs font-semibold text-accent-magenta dark:text-accent-blue mb-1">
                      Language: {project.language}
                    </p>
                  )}
                  <div className="flex items-center text-xs text-gray-500 dark:text-gray-400 mb-3">
                    <i className="bi bi-star-fill mr-1 text-yellow-400"></i> {project.stargazers_count}
                    <i className="bi bi-git mr-1 ml-3 text-gray-600 dark:text-gray-300"></i> {project.forks_count}
                  </div>
                  {project.topics && project.topics.length > 0 && (
                    <div className="mb-3">
                      {project.topics.slice(0, 3).map(topic => (
                        <span key={topic} className="inline-block bg-accent-teal/20 text-accent-teal dark:bg-accent-blue/20 dark:text-accent-blue text-xs px-2 py-0.5 rounded-full mr-1 mb-1">
                          {topic}
                        </span>
                      ))}
                    </div>
                  )}
                  <a 
                    href={project.html_url} 
                    target="_blank" 
                    rel="noopener noreferrer" 
                    className="text-sm font-medium text-accent-blue dark:text-accent-teal hover:underline mt-auto self-start"
                  >
                    {t('projects.viewProject')} <i className="bi bi-box-arrow-up-right ml-1"></i>
                  </a>
                </motion.div>
              ))}
            </div>
            {/* Adicionar link para o GitHub se houver mais projetos */}
            <div className="text-center mt-12">
                <a href={githubProfileUrl} target="_blank" rel="noopener noreferrer" className="btn-secondary">
                    {t('projects.seeMore')} <i className="bi bi-github ml-2"></i>
                </a>
            </div>
          </div>
        </section>

        {/* Licenses & Certifications Section (Type 2) - Show More/Less with Framer Motion */}
        <section id="certifications" ref={certificationsSectionRef} className={`py-16 md:py-24 bg-light-bg text-light-text dark:bg-brand-purple-dark dark:text-dark-text`}>
          <div className="container mx-auto px-6">
            <h2 className={`text-3xl md:text-4xl font-bold text-center mb-12 flex items-center justify-center`}>
              <AcademicCapIcon className="h-8 w-8 mr-3 text-accent-teal" />
              {t('certifications.title')}
            </h2>
            
            <motion.div 
              layout 
              className="grid md:grid-cols-2 gap-8 max-w-5xl mx-auto"
            >
              <AnimatePresence>
                {allCertificatesData.slice(0, showAllCertificates ? allCertificatesData.length : initialCertificatesToShow).map((cert, index) => (
                  <motion.div
                    key={cert.key}
                    custom={index} 
                    variants={cardVariants}
                    initial="hidden"
                    animate="visible"
                    exit="exit"
                    layout 
                    className={`p-6 rounded-xl shadow-xl card-hover flex flex-col bg-white text-light-text dark:bg-brand-purple-light dark:text-dark-text`}
                  >
                    <div className="flex items-center mb-4">
                      <img src={cert.logo} alt={`${cert.issuer} logo`} className="h-10 w-10 mr-4 object-contain dark:bg-slate-300 dark:p-1 dark:rounded-sm" />
                      <div>
                        <h3 className="text-lg md:text-xl font-bold leading-tight">{cert.name}</h3>
                        <p className={`text-sm font-semibold text-gray-600 dark:text-gray-300`}>{cert.issuer}</p>
                      </div>
                    </div>
                    <p className={`text-xs text-gray-500 dark:text-gray-400 mb-1`}>{cert.date}</p>
                    <p className={`text-xs text-gray-500 dark:text-gray-400 mb-3`}>
                      ID: {cert.credentialId}
                    </p>
                    
                    <div className="mb-4 mt-auto">
                      <p className={`text-sm font-semibold mb-1 text-gray-700 dark:text-gray-200`}>Skills:</p>
                      <div className="flex flex-wrap gap-2">
                        {/* Certifique-se de que cert.skills é um array */}
                        {Array.isArray(cert.skills) && cert.skills.map(skill => (
                          <span key={skill} className="text-xs bg-accent-teal/20 text-accent-teal dark:bg-accent-blue/20 dark:text-accent-blue px-2 py-1 rounded-full">{skill}</span>
                        ))}
                      </div>
                    </div>
                    <a 
                      href={cert.credentialUrl || '#'} // Usando cert.credentialUrl como no código anterior, ou cert.link se for o caso. Adicionado fallback para '#'
                      target="_blank" 
                      rel="noopener noreferrer" 
                      className="text-sm font-medium text-accent-magenta dark:text-accent-blue hover:underline self-start"
                    >
                      {t('certifications.showCredentialText')} <i className="bi bi-box-arrow-up-right ml-1"></i>
                    </a>
                  </motion.div>
                ))}
              </AnimatePresence>
            </motion.div>

            {allCertificatesData.length > initialCertificatesToShow && (
              <div className="text-center mt-12">
                <motion.button
                  onClick={toggleShowAllCertificates}
                  className="btn-secondary"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  {showAllCertificates ? t('certifications.showLess') : t('certifications.showMore')}
                </motion.button>
              </div>
            )}
          </div>
        </section>

        {/* Contact Section (Type 2 - Redesigned) */}
        <section id="contact" className={`py-16 md:py-24 bg-gray-50 text-light-text dark:bg-brand-purple dark:text-dark-text`}>
          <div className="container mx-auto px-6">
            <h2 className="text-3xl md:text-4xl font-bold text-center mb-12 flex items-center justify-center">
              <EnvelopeIcon className="h-8 w-8 mr-3 text-accent-blue dark:text-accent-teal" /> {t('contact.title')}
            </h2>
            <div className="max-w-xl mx-auto bg-white dark:bg-brand-purple-light rounded-xl shadow-lg p-8">
              <form 
                action="https://formspree.io/f/mwkajvjd" 
                method="POST" 
                className="space-y-4"
              >
                <div>
                  <label htmlFor="name" className="block text-sm font-semibold mb-1">{t('contact.form.name')}</label>
                  <input 
                    type="text" 
                    name="name" 
                    id="name" 
                    required 
                    className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-accent-teal dark:focus:ring-accent-blue transition-all"
                    placeholder={t('contact.form.namePlaceholder')}
                  />
                </div>
                <div>
                  <label htmlFor="email" className="block text-sm font-semibold mb-1">{t('contact.form.email')}</label>
                  <input 
                    type="email" 
                    name="email" 
                    id="email" 
                    required 
                    className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-accent-teal dark:focus:ring-accent-blue transition-all"
                    placeholder={t('contact.form.emailPlaceholder')}
                  />
                </div>
                <div>
                  <label htmlFor="message" className="block text-sm font-semibold mb-1">{t('contact.form.message')}</label>
                  <textarea 
                    name="message" 
                    id="message" 
                    rows="4" 
                    required 
                    className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-accent-teal dark:focus:ring-accent-blue transition-all"
                    placeholder={t('contact.form.messagePlaceholder')}
                  ></textarea>
                </div>
                <div className="flex justify-center">
                  <button 
                    type="submit" 
                    className="btn-primary w-full flex items-center justify-center gap-2"
                  >
                    <i className="bi bi-paperplane-fill"></i> {t('contact.form.sendButton')}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </section>
      </main>

      {/* Footer (Type 2 - Redesigned) */}
      <footer className="py-8 bg-brand-purple-light dark:bg-brand-purple-dark text-light-text dark:text-dark-text">
        <div className="container mx-auto px-6">
          <div className="flex flex-col md:flex-row items-center justify-between mb-4">
            <div className="text-center md:text-left mb-4 md:mb-0">
              <p className="text-sm">&copy; {new Date().getFullYear()} {t('footer.rights')}</p>
            </div>
            <div className="flex gap-4 justify-center md:justify-end">
              <a href={linkedinProfileUrl} target="_blank" rel="noopener noreferrer" className="text-gray-600 dark:text-gray-300 hover:text-accent-blue dark:hover:text-accent-teal transition-colors">
                <i className="bi bi-linkedin"></i>
              </a>
              <a href={githubProfileUrl} target="_blank" rel="noopener noreferrer" className="text-gray-600 dark:text-gray-300 hover:text-accent-blue dark:hover:text-accent-teal transition-colors">
                <i className="bi bi-github"></i>
              </a>
              <a href={`mailto:${emailAddress}`} className="text-gray-600 dark:text-gray-300 hover:text-accent-blue dark:hover:text-accent-teal transition-colors">
                <i className="bi bi-envelope"></i>
              </a>
            </div>
          </div>
          <div className="text-center">
            <a 
              href={cvFileUrl} 
              download 
              className="text-sm text-gray-500 dark:text-gray-400 hover:text-accent-blue dark:hover:text-accent-teal transition-colors"
            >
              {t('footer.downloadCV')} <i className="bi bi-download ml-1"></i>
            </a>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
