import React, { useState, useEffect, Suspense } from 'react';
import { useTranslation } from 'react-i18next';
import { SunIcon, MoonIcon, LanguageIcon } from '@heroicons/react/24/outline'; // Using Heroicons for icons

// Placeholder for 3D elements or visual flourishes
const FloatingShape = ({ className }) => (
  <div className={`absolute w-16 h-16 rounded-full opacity-20 animate-float ${className}`}></div>
);

function App() {
  const { t, i18n } = useTranslation();
  const [darkMode, setDarkMode] = useState(() => {
    const savedMode = localStorage.getItem('darkMode');
    // Default to true (dark mode) if no saved mode
    return savedMode ? JSON.parse(savedMode) : true;
  });

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
    localStorage.setItem('darkMode', JSON.stringify(darkMode));
  }, [darkMode]);

  const toggleDarkMode = () => setDarkMode(!darkMode);

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
    // Optionally, save language preference to localStorage
    // localStorage.setItem('i18nextLng', lng); 
  };

  const currentLanguage = i18n.language;

  // Ensure English is set if no language is detected and it's not already English
  // This reinforces English as the initial default if LanguageDetector doesn't pick a preference.
  useEffect(() => {
    if (!localStorage.getItem('i18nextLng') && i18n.language.split('-')[0] !== 'en') {
      // i18n.changeLanguage('en'); // Uncomment this line if you want to force English on first visit regardless of browser lang
    }
  }, [i18n]);


  // Placeholder data for sections
  const projects = [
    { id: 1, title: "Project Alpha", description: "A cutting-edge web application.", tech: ["React", "Node.js", "Tailwind CSS"], image: "https://via.placeholder.com/300x200/FF00FF/FFFFFF?Text=Project+Alpha" },
    { id: 2, title: "Service Beta", description: "Mobile-first platform for services.", tech: ["Vue.js", "Firebase", "SASS"], image: "https://via.placeholder.com/300x200/00A9FF/FFFFFF?Text=Service+Beta" },
    { id: 3, title: "Tool Gamma", description: "Data analysis and visualization tool.", tech: ["Python", "Flask", "D3.js"], image: "https://via.placeholder.com/300x200/00FFC6/000000?Text=Tool+Gamma" },
  ];

  const skills = ["JavaScript (ES6+)", "React.js", "Node.js", "Express.js", "HTML5", "CSS3", "Tailwind CSS", "Git", "REST APIs", "SASS/LESS"];

  return (
    <div className="min-h-screen flex flex-col relative overflow-x-hidden">
      {/* Subtle floating elements for depth */}
      <FloatingShape className="bg-accent-blue top-1/4 left-10" />
      <FloatingShape className="bg-accent-magenta bottom-1/4 right-10" />
      <FloatingShape className="bg-accent-teal top-1/2 left-1/3" />


      {/* Header */}
      <header className="sticky top-0 z-50 py-4 px-6 md:px-10 shadow-md bg-light-bg/80 dark:bg-brand-purple-dark/80 backdrop-blur-md">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-2xl font-bold bg-clip-text text-transparent bg-gradient-accent">
            {t('siteTitle').split(' | ')[0]}
          </h1>
          <nav className="flex items-center space-x-4">
            {/* Language Switcher */}
            <button
              onClick={() => changeLanguage(currentLanguage === 'en' ? 'pt' : 'en')}
              className="p-2 rounded-full hover:bg-gray-200 dark:hover:bg-brand-purple-light transition-colors"
              aria-label={t('languageSwitcher.changeTo')}
            >
              <LanguageIcon className="h-6 w-6" />
            </button>
            {/* Dark Mode Toggle */}
            <button
              onClick={toggleDarkMode}
              className="p-2 rounded-full hover:bg-gray-200 dark:hover:bg-brand-purple-light transition-colors"
              aria-label={darkMode ? t('themeSwitcher.light') : t('themeSwitcher.dark')}
            >
              {darkMode ? <SunIcon className="h-6 w-6 text-yellow-400" /> : <MoonIcon className="h-6 w-6 text-brand-purple" />}
            </button>
          </nav>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-grow">
        {/* Hero Section */}
        <section id="hero" className="relative py-20 md:py-32 text-center bg-gradient-purple-dark dark:bg-gradient-to-br dark:from-brand-purple-dark dark:to-brand-purple flex flex-col items-center justify-center min-h-[calc(100vh-64px)] overflow-hidden">
          <div className="absolute inset-0 opacity-10 dark:opacity-20">
            {/* Placeholder for more complex background visuals */}
            <div className="absolute top-0 left-0 w-1/2 h-1/2 bg-accent-blue rounded-full filter blur-3xl animate-pulseGlow animation-delay-2000"></div>
            <div className="absolute bottom-0 right-0 w-1/3 h-1/3 bg-accent-magenta rounded-full filter blur-3xl animate-pulseGlow"></div>
          </div>
          <div className="container mx-auto px-6 z-10 animate-fadeInUp">
            <h2 className="text-4xl md:text-6xl font-black mb-4 text-white dark:text-dark-text">
              {t('hero.greeting')} <span className="bg-clip-text text-transparent bg-gradient-accent">{t('hero.name')}</span>
            </h2>
            <p className="text-xl md:text-2xl font-bold mb-6 text-gray-200 dark:text-gray-300">{t('hero.title')}</p>
            <p className="text-lg md:text-xl max-w-2xl mx-auto mb-10 text-gray-300 dark:text-gray-400">
              {t('hero.subtitle')}
            </p>
            <div className="space-x-4">
                <a href="#projects" className="btn-primary">
                {t('hero.ctaButton')}
                </a>
                <a 
                    href="https://github.com/DevJpLg" 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="btn-secondary"
                >
                    {t('hero.githubLink')}
                </a>
            </div>
          </div>
        </section>

        {/* Projects Section */}
        <section id="projects" className={`py-16 md:py-24 ${darkMode ? 'section-gradient-dark' : 'section-gradient-light'}`}>
          <div className="container mx-auto px-6">
            <h2 className="text-3xl md:text-4xl font-bold text-center mb-4">{t('sections.projects.title')}</h2>
            <p className="text-lg text-center max-w-xl mx-auto mb-12 text-gray-600 dark:text-gray-400">{t('sections.projects.description')}</p>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
              {projects.map((project) => (
                <div key={project.id} className="bg-white dark:bg-brand-purple-light rounded-xl shadow-lg overflow-hidden card-hover-glow animate-fadeInUp" style={{animationDelay: `${project.id * 100}ms`}}>
                  <img src={project.image} alt={project.title} className="w-full h-48 object-cover"/>
                  <div className="p-6">
                    <h3 className="text-xl font-bold mb-2">{project.title}</h3>
                    <p className="text-sm text-gray-700 dark:text-gray-300 mb-3">{project.description}</p>
                    <div className="flex flex-wrap gap-2 mb-4">
                        {project.tech.map(tech => (
                            <span key={tech} className="text-xs bg-accent-teal/20 text-accent-teal dark:bg-accent-blue/20 dark:text-accent-blue px-2 py-1 rounded-full">{tech}</span>
                        ))}
                    </div>
                    <a href="#" className="text-accent-magenta dark:text-accent-blue hover:underline font-semibold">View Details &rarr;</a>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </section>

        {/* Skills Section */}
        <section id="skills" className={`py-16 md:py-24 ${!darkMode ? 'section-gradient-dark bg-brand-purple-dark text-white' : 'section-gradient-light bg-light-bg text-light-text'}`}>
          <div className="container mx-auto px-6">
            <h2 className={`text-3xl md:text-4xl font-bold text-center mb-4 ${!darkMode ? 'text-white' : 'text-light-text'}`}>{t('sections.skills.title')}</h2>
            <p className={`text-lg text-center max-w-xl mx-auto mb-12 ${!darkMode ? 'text-gray-300' : 'text-gray-600'}`}>{t('sections.skills.description')}</p>
            <div className="flex flex-wrap justify-center gap-4">
              {skills.map((skill, index) => (
                <div key={skill} className={`px-6 py-3 rounded-lg shadow-md card-hover animate-fadeInUp ${!darkMode ? 'bg-brand-purple-light text-dark-text' : 'bg-white text-light-text'}`} style={{animationDelay: `${index * 50}ms`}}>
                  {skill}
                </div>
              ))}
            </div>
          </div>
        </section>
        
        {/* Placeholder for Testimonials and Trust Logos */}
        <section id="testimonials" className={`py-16 md:py-24 ${darkMode ? 'section-gradient-dark' : 'section-gradient-light'}`}>
            <div className="container mx-auto px-6 text-center">
                <h2 className="text-3xl md:text-4xl font-bold mb-4">{t('sections.testimonials.title')}</h2>
                <p className="text-lg max-w-xl mx-auto mb-12 text-gray-600 dark:text-gray-400">{t('sections.testimonials.description')}</p>
                <p className="italic text-gray-500 dark:text-gray-500">(Testimonials section coming soon...)</p>
                {/* Add rounded cards for testimonials here */}
            </div>
        </section>

        <section id="trust-logos" className={`py-16 md:py-24 ${!darkMode ? 'section-gradient-dark bg-brand-purple-dark' : 'section-gradient-light bg-light-bg'}`}>
            <div className="container mx-auto px-6 text-center">
                <h2 className={`text-2xl font-semibold mb-8 ${!darkMode ? 'text-gray-300' : 'text-gray-700'}`}>Trusted By / Featured In</h2>
                <div className="flex flex-wrap justify-center items-center gap-8 md:gap-12 opacity-60">
                    {/* Placeholder logos */}
                    <span className={`text-xl font-mono ${!darkMode ? 'text-gray-400' : 'text-gray-500'}`}>LOGO_A</span>
                    <span className={`text-xl font-mono ${!darkMode ? 'text-gray-400' : 'text-gray-500'}`}>LOGO_B</span>
                    <span className={`text-xl font-mono ${!darkMode ? 'text-gray-400' : 'text-gray-500'}`}>LOGO_C</span>
                    <span className={`text-xl font-mono ${!darkMode ? 'text-gray-400' : 'text-gray-500'}`}>LOGO_D</span>
                </div>
            </div>
        </section>

      </main>

      {/* Footer */}
      <footer className="py-8 text-center bg-brand-purple-dark text-gray-400">
        <div className="container mx-auto px-6">
          <p>{t('footer.text')}</p>
          <p className="text-sm mt-2">
            Built with React, Tailwind CSS, and ❤️ by <a href="https://github.com/DevJpLg" target="_blank" rel="noopener noreferrer" className="text-accent-teal hover:underline">DevJpLg</a>.
          </p>
        </div>
      </footer>
    </div>
  );
}

export default function SuspendedApp() {
  return (
    <Suspense fallback={<div className="flex justify-center items-center min-h-screen text-xl">Loading translations...</div>}>
      <App />
    </Suspense>
  );
}
