import React, { useState, useEffect, Suspense, useRef } from 'react';
import { useTranslation } from 'react-i18next';
import { motion, AnimatePresence } from 'framer-motion';
import {
  LanguageIcon, BriefcaseIcon, AcademicCapIcon,
  SparklesIcon, CodeBracketIcon, LinkIcon, RocketLaunchIcon,
  ChatBubbleLeftRightIcon, ArrowDownTrayIcon, EnvelopeIcon,
  CpuChipIcon,
  Bars3Icon, // Added for hamburger menu
  XMarkIcon,  // Added for close button
  HomeIcon,   // Added for nav
  UserCircleIcon // Added for nav
} from '@heroicons/react/24/outline';
import Sidebar from './components/Sidebar'; // Assuming Sidebar.js is in src/components

const FloatingShape = ({ className }) => (
  <div className={`absolute w-12 h-12 md:w-16 md:h-16 rounded-full opacity-10 dark:opacity-20 animate-float ${className}`}></div>
);

// New Component for Animated Code Lines
const pseudoCodeLines = [
  "const fetchData = async (url) => { const response = await fetch(url); return response.json(); };",
  "useEffect(() => { document.title = `Page Loaded: ${Date.now()}`; }, []);",
  "class User { constructor(name) { this.name = name; } greet() { console.log(`Hello, ${this.name}`); } }",
  "const numbers = [1, 2, 3, 4, 5]; const sum = numbers.reduce((acc, val) => acc + val, 0);",
  "if (status === 'completed' && result.isValid) { processResult(result); } else { handleError(status); }",
  "function* idGenerator() { let id = 0; while(true) yield id++; }",
  "const pipeline = data.filter(item => item.active).map(item => transform(item)).sort((a, b) => a.value - b.value);",
  "import { motion } from 'framer-motion';",
  "const API_KEY = process.env.REACT_APP_API_KEY;",
  "return <div className='container'>{children}</div>;"
];

const AnimatedCodeLine = ({ text, yPosition, duration, direction = 1 }) => {
  const initialX = direction === 1 ? "-100%" : "100%";
  const animateX = direction === 1 ? "100%" : "-100%";

  return (
    <motion.div
      className="absolute whitespace-nowrap font-mono text-xs text-accent-teal/20 dark:text-accent-teal/30"
      style={{ top: yPosition, left: 0, right: 0, willChange: 'transform' }} // left/right 0 to ensure it spans width for x calc
      initial={{ x: initialX }}
      animate={{ x: animateX }}
      transition={{
        duration: duration,
        ease: "linear",
        repeat: Infinity,
        repeatType: "loop",
      }}
    >
      {text}
    </motion.div>
  );
};

const AnimatedCodeLinesBackground = ({ numLines = 8 }) => {
  const lines = React.useMemo(() => {
    const generatedLines = [];
    for (let i = 0; i < numLines; i++) {
      generatedLines.push({
        id: i,
        text: pseudoCodeLines[Math.floor(Math.random() * pseudoCodeLines.length)],
        yPosition: `${5 + Math.random() * 90}%`, // Spread vertically from 5% to 95%
        duration: 25 + Math.random() * 25, // Vary speeds (25s to 50s)
        direction: Math.random() > 0.5 ? 1 : -1, // Random direction
      });
    }
    return generatedLines;
  }, [numLines]);

  return (
    <>
      {lines.map(line => (
        <AnimatedCodeLine
          key={line.id}
          text={line.text}
          yPosition={line.yPosition}
          duration={line.duration}
          direction={line.direction}
        />
      ))}
    </>
  );
};
// End of New Component for Animated Code Lines


const sections = [
  { id: 'home', title: 'Home', icon: HomeIcon },
  { id: 'about', title: 'About', icon: UserCircleIcon },
  { id: 'experience', title: 'Experience', icon: BriefcaseIcon },
  { id: 'skills', title: 'Skills', icon: CpuChipIcon },
  { id: 'certifications', title: 'Certifications', icon: SparklesIcon },
  { id: 'projects', title: 'Projects', icon: CodeBracketIcon },
  { id: 'education', title: 'Education', icon: AcademicCapIcon },
  { id: 'contact', title: 'Contact', icon: ChatBubbleLeftRightIcon },
];

function App() {
  const { t, i18n } = useTranslation();
  const [currentLanguage, setCurrentLanguage] = useState(i18n.language.split('-')[0]);
