import React, { useState, useEffect, Suspense, useRef } from 'react'; // Adicionado useRef
import { useTranslation } from 'react-i18next';
import { motion, AnimatePresence } from 'framer-motion';
import {
  SunIcon, MoonIcon, LanguageIcon, BriefcaseIcon, AcademicCapIcon,
  SparklesIcon, CodeBracketIcon, LinkIcon, RocketLaunchIcon,
  ChatBubbleLeftRightIcon, ArrowDownTrayIcon // Adicionado ArrowDownTrayIcon
} from '@heroicons/react/24/outline';

// Placeholder for 3D elements or visual flourishes
const FloatingShape = ({ className }) => (
  <div className={`absolute w-12 h-12 md:w-16 md:h-16 rounded-full opacity-10 dark:opacity-20 animate-float ${className}`}></div>
);

function App() {
  const { t, i18n } = useTranslation();
  const [darkMode, setDarkMode] = useState(() => {
    const savedMode = localStorage.getItem('darkMode');
    return savedMode ? JSON.parse(savedMode) : true; // Dark mode is default
  });

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
    localStorage.setItem('darkMode', JSON.stringify(darkMode));
  }, [darkMode]);

  const toggleDarkMode = () => setDarkMode(!darkMode);

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };

  const currentLanguage = i18n.language;

  const linkedinProfileUrl = "https://www.linkedin.com/in/joaopedrolopesgoncalves/";
  const githubProfileUrl = "https://github.com/DevJpLg";
  const technipFmcLogoUrl = "/images/technipfmc.svg"; // Ensure this path is correct
  const profilePhotoUrl = "/images/profile.jpg"; // Path to your profile photo
  const cvFileUrl = "/Joao_Pedro_Goncalves_CV.pdf"; // Caminho para o arquivo do CV na pasta public


  // Placeholder for actual projects from GitHub
  const projectsPlaceholder = [
    { id: 1, title: "GitHub Project 1", description: "Description for project 1 from GitHub.", tech: ["React", "Node.js"], image: "https://via.placeholder.com/300x200/FF00FF/FFFFFF?Text=Project+1", link: "#" },
    { id: 2, title: "GitHub Project 2", description: "Description for project 2 from GitHub.", tech: ["Python", "API"], image: "https://via.placeholder.com/300x200/00A9FF/FFFFFF?Text=Project+2", link: "#" },
  ];


  // State for certificate pagination
  const [currentCertificatePage, setCurrentCertificatePage] = useState(0);
  const certificates = t('certifications.certificates', { returnObjects: true }) || []; // Ensure certificates is an array

  const totalCertificatePages = certificates.length;

  const handlePrevCertificate = () => {
    setCurrentCertificatePage((prevPage) => (prevPage === 0 ? totalCertificatePages - 1 : prevPage - 1));
  };

  const handleNextCertificate = () => {
    setCurrentCertificatePage((prevPage) => (prevPage === totalCertificatePages - 1 ? 0 : prevPage + 1));
  };

  const handleDotClick = (index) => {
    setCurrentCertificatePage(index);
  };

  // State for showing/hiding extra certificates
  const [showAllCertificates, setShowAllCertificates] = useState(false);
  const allCertificatesData = t('certifications.certificates', { returnObjects: true }) || [];
  const initialCertificatesToShow = 4;

  const certificationsSectionRef = useRef(null); // Criar a ref para a seção

  const toggleShowAllCertificates = () => {
    // Se estamos prestes a mostrar menos (ou seja, showAllCertificates é true agora)
    if (showAllCertificates && certificationsSectionRef.current) {
      certificationsSectionRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
    setShowAllCertificates(!showAllCertificates);
  };

  // Variantes para animação dos cards
  const cardVariants = {
    hidden: { opacity: 0, y: 20, scale: 0.95 },
    visible: (i) => ({
      opacity: 1,
      y: 0,
      scale: 1,
      transition: {
        delay: i * 0.05, // Delay escalonado para cada card
        duration: 0.3,
        ease: "easeOut"
      }
    }),
    exit: { 
      opacity: 0, 
      y: -10, 
      scale: 0.95,
      transition: { 
        duration: 0.2,
        ease: "easeIn"
      } 
    }
  };

  // Variantes para animação das categorias e skills
  const sectionVariant = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.5,
        when: "beforeChildren", // Anima filhos depois do pai
        staggerChildren: 0.1 // Delay entre animação de filhos
      }
    }
  };

  const skillItemVariant = {
    hidden: { opacity: 0, scale: 0.8, y: 10 },
    visible: {
      opacity: 1,
      scale: 1,
      y: 0,
      transition: { duration: 0.3, ease: "easeOut" }
    }
  };

  return (
    <div className="min-h-screen flex flex-col relative overflow-x-hidden bg-light-bg dark:bg-brand-purple-dark text-light-text dark:text-dark-text">
      <FloatingShape className="bg-accent-blue top-1/4 left-5 md:left-10" />
      <FloatingShape className="bg-accent-magenta bottom-1/4 right-5 md:right-10 animation-delay-2000" />
      <FloatingShape className="bg-accent-teal top-1/2 left-1/3 animation-delay-4000" />

      <header className="sticky top-0 z-50 py-4 px-4 md:px-10 shadow-lg bg-light-bg/80 dark:bg-brand-purple-dark/80 backdrop-blur-md">
        <div className="container mx-auto flex justify-between items-center">
          <a href="#hero" className="text-lg md:text-xl font-bold bg-clip-text text-transparent bg-gradient-accent">
            {t('hero.name')} {/* Display full name from translation */}
          </a>
          <nav className="flex items-center space-x-2 md:space-x-4">
            <button
              onClick={() => changeLanguage(currentLanguage.startsWith('en') ? 'pt' : 'en')}
              className="p-2 rounded-full hover:bg-gray-200 dark:hover:bg-brand-purple-light transition-colors"
              aria-label={t('languageSwitcher.changeTo')}
            >
              <LanguageIcon className="h-5 w-5 md:h-6 md:w-6" />
            </button>
            <button
              onClick={toggleDarkMode}
              className="p-2 rounded-full hover:bg-gray-200 dark:hover:bg-brand-purple-light transition-colors"
              aria-label={darkMode ? t('themeSwitcher.light') : t('themeSwitcher.dark')}
            >
              {darkMode ? <SunIcon className="h-5 w-5 md:h-6 md:w-6 text-yellow-400" /> : <MoonIcon className="h-5 w-5 md:h-6 md:w-6 text-brand-purple" />}
            </button>
          </nav>
        </div>
      </header>

      <main className="flex-grow">
        {/* Hero Section */}
        <section id="hero" className="relative py-20 md:py-32 text-center bg-gradient-purple-dark dark:bg-gradient-to-br dark:from-brand-purple-dark dark:to-brand-purple flex flex-col items-center justify-center min-h-[calc(100vh-64px)] overflow-hidden px-4">
          <div className="absolute inset-0 opacity-10 dark:opacity-20">
            <div className="absolute top-0 left-0 w-1/2 h-1/2 bg-accent-blue rounded-full filter blur-3xl animate-pulseGlow animation-delay-2000"></div>
            <div className="absolute bottom-0 right-0 w-1/3 h-1/3 bg-accent-magenta rounded-full filter blur-3xl animate-pulseGlow"></div>
          </div>
          <div className="container mx-auto z-10 animate-fadeInUp">
            <h1 className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl font-black mb-3 text-white dark:text-dark-text">
              {t('hero.greeting')} <span className="bg-clip-text text-transparent bg-gradient-accent">{t('hero.name')}</span>
            </h1>
            <p className="text-lg sm:text-xl md:text-2xl font-semibold mb-5 text-gray-200 dark:text-gray-300 max-w-3xl mx-auto">{t('hero.title')}</p>
            <p className="text-md sm:text-lg md:text-xl max-w-2xl mx-auto mb-8 text-gray-300 dark:text-gray-400">
              {t('hero.subtitle')}
            </p>
            <div className="flex flex-wrap justify-center items-center gap-4"> {/* Usando flex-wrap e gap */}
                <a href={githubProfileUrl} target="_blank" rel="noopener noreferrer" className="btn-primary inline-block">
                  <CodeBracketIcon className="h-5 w-5 inline-block mr-2" /> {t('hero.ctaButton')}
                </a>
                <a 
                    href={cvFileUrl}
                    download // Atributo para forçar o download
                    className="btn-secondary inline-block" // Pode usar btn-primary ou um novo estilo
                >
                    <ArrowDownTrayIcon className="h-5 w-5 inline-block mr-2" /> {t('hero.downloadCV')}
                </a>
            </div>
          </div>
        </section>

        {/* About Me Section (Type 1 - Redesigned) */}
        <section id="about" className={`py-16 md:py-24 bg-gray-50 text-light-text dark:bg-brand-purple dark:text-dark-text overflow-hidden`}>
          <div className="container mx-auto px-6">
            <h2 className="text-3xl md:text-4xl font-bold text-center mb-12 flex items-center justify-center">
              <i className="bi bi-person-circle mr-3 text-3xl md:text-4xl text-accent-magenta"></i> {t('about.title')}
            </h2>
            <div className="flex flex-col md:flex-row items-center md:items-start gap-8 md:gap-12 animate-fadeInUp">
              <div className="w-48 h-48 md:w-60 md:h-60 lg:w-72 lg:h-72 flex-shrink-0 rounded-full overflow-hidden shadow-2xl border-4 border-accent-teal dark:border-accent-blue transform transition-all duration-500 hover:scale-105">
                <img 
                  src={profilePhotoUrl} 
                  alt={t('hero.name')} 
                  className="w-full h-full object-cover"
                />
              </div>
              <div className="text-center md:text-left flex-grow">
                <p className="text-2xl md:text-3xl font-semibold mb-4">{t('about.intro')}</p>
                <p className="mb-4 text-base md:text-lg leading-relaxed text-gray-700 dark:text-gray-300">{t('about.paragraph1')}</p>
                <p className="mb-6 text-base md:text-lg leading-relaxed text-gray-700 dark:text-gray-300">{t('about.paragraph2')}</p>
                <div className="space-y-2 text-sm md:text-base text-gray-600 dark:text-gray-400">
                  <a 
                    href={t('about.locationUrl')} 
                    target="_blank" 
                    rel="noopener noreferrer" 
                    className="flex items-center justify-center md:justify-start hover:text-accent-blue dark:hover:text-accent-teal transition-colors"
                  >
                    <i className="bi bi-geo-alt-fill mr-2 text-accent-teal"></i> {t('about.location')}
                  </a>
                  <p className="flex items-center justify-center md:justify-start">
                    <i className="bi bi-people-fill mr-2 text-accent-teal"></i> {t('about.connections')}
                  </p>
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* Skills Section (Type 2 - Redesigned) */}
        <motion.section 
          id="skills" 
          className={`py-16 md:py-24 bg-light-bg text-light-text dark:bg-brand-purple-dark dark:text-dark-text`}
          variants={sectionVariant} // sectionVariant from previous step
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.05 }} // Trigger when a small part is visible
        >
          <div className="container mx-auto px-6">
            <h2 className={`text-3xl md:text-4xl font-bold text-center mb-12 flex items-center justify-center`}>
                <RocketLaunchIcon className="h-8 w-8 mr-3 text-accent-magenta" /> {t('skills.title')}
            </h2>

            {/* Horizontal Bar Chart Section - REMOVIDO */}
            {/*
            <motion.div 
              className="mb-16 md:mb-20 max-w-3xl mx-auto"
              variants={barChartVariant}
            >
              <h3 className="text-xl md:text-2xl font-semibold mb-8 text-center md:text-left text-gray-700 dark:text-gray-200">
                {t('skills.barChart.title')}
              </h3>
              <div className="space-y-5">
                {(t('skills.barChart.items', { returnObjects: true }) || []).map((skill, index) => (
                  <motion.div 
                    key={skill.name} 
                    className="flex items-center"
                  >
                    <span className="w-32 sm:w-36 text-sm sm:text-base font-medium text-gray-700 dark:text-gray-300 pr-3 text-right flex-shrink-0">
                      {skill.name}
                    </span>
                    <div className="flex-1 bg-gray-300 dark:bg-gray-600 rounded-full h-6 md:h-7 overflow-hidden relative">
                      <motion.div
                        className={`absolute top-0 left-0 h-full rounded-full ${skill.color || 'bg-accent-teal'}`}
                        custom={skill.percentage}
                        variants={barItemVariant}
                        initial="hidden"
                        whileInView="visible"
                        viewport={{ once: true, amount: 0.1 }}
                      >
                        <motion.span 
                          className="absolute right-2 top-1/2 -translate-y-1/2 text-xs sm:text-sm font-bold text-white"
                          variants={percentageTextVariant}
                          initial="hidden"
                          whileInView="visible"
                          viewport={{ once: true, amount: 0.1 }} 
                        >
                          {skill.percentage}%
                        </motion.span>
                      </motion.div>
                    </div>
                  </motion.div>
                ))}
              </div>
            </motion.div>
            */}
            
            {/* Skill Categories Section */}
            <div className="space-y-12 md:space-y-16">
              {(t('skills.categories', { returnObjects: true }) || []).map((category, catIndex) => (
                <motion.div 
                  key={category.name}
                  variants={sectionVariant} // Pode usar a mesma variante ou uma específica para categorias
                >
                  <h3 className="text-2xl md:text-3xl font-semibold mb-8 text-center md:text-left flex items-center justify-center md:justify-start">
                    <i className={`${category.bsIcon} mr-3 text-2xl md:text-3xl text-accent-teal dark:text-accent-blue`}></i>
                    {category.name}
                  </h3>
                  <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 xl:grid-cols-6 gap-4 md:gap-6">
                    {(category.items || []).map((skill, skillIndex) => (
                      <motion.div
                        key={skill.name}
                        className="flex flex-col items-center p-4 bg-white dark:bg-brand-purple-light rounded-lg shadow-lg card-hover-glow text-center"
                        variants={skillItemVariant}
                        // whileInView e viewport aqui também se quiser animação individual ao rolar para cada skill
                        // initial="hidden" // Já herdado do pai se using when: "beforeChildren"
                        // animate="visible"
                      >
                        <img 
                          src={skill.icon} 
                          alt={`${skill.name} icon`} 
                          className="h-12 w-12 md:h-16 md:w-16 mb-3 object-contain filter dark:brightness-0 dark:invert-[75%] dark:sepia-[15%] dark:saturate-[500%] dark:hue-rotate-[180deg]"
                          // O filtro acima é uma tentativa de tornar SVGs escuros visíveis no modo escuro. Ajuste conforme necessário.
                          // Se seus SVGs já são adequados para ambos os modos, remova o filtro.
                        />
                        <p className="text-sm md:text-base font-medium text-gray-700 dark:text-gray-200">{skill.name}</p>
                      </motion.div>
                    ))}
                  </div>
                </motion.div>
              ))}
            </div>
          </div>
        </motion.section>

        {/* Experience Section (Type 2) */}
        <section id="experience" className={`py-16 md:py-24 bg-light-bg text-light-text dark:bg-brand-purple-dark dark:text-dark-text`}>
          <div className="container mx-auto px-6">
            <h2 className={`text-3xl md:text-4xl font-bold text-center mb-12 flex items-center justify-center`}>
              <BriefcaseIcon className="h-8 w-8 mr-3 text-accent-blue" /> {t('experience.title')}
            </h2>
            <div className="max-w-3xl mx-auto">
              <div className={`p-6 md:p-8 rounded-xl shadow-xl card-hover-glow bg-white text-light-text dark:bg-brand-purple-light dark:text-dark-text`}>
                <div className="flex items-start sm:items-center mb-2 flex-col sm:flex-row">
                  <img
                    src={technipFmcLogoUrl}
                    alt="TechnipFMC Logo"
                    className="h-10 w-10 sm:h-12 sm:w-12 mr-0 sm:mr-4 mb-2 sm:mb-0 rounded-sm object-contain" 
                  />
                  <div>
                    <h3 className="text-2xl font-bold ">{t('experience.technipfmc.role')}</h3>
                    <p className="text-lg font-semibold text-accent-magenta dark:text-accent-teal ">{t('experience.technipfmc.company')}</p>
                  </div>
                </div>
                <p className="text-sm text-gray-500 dark:text-gray-400 mb-1">{t('experience.technipfmc.duration')}</p>
                <p className="text-sm text-gray-500 dark:text-gray-400 mb-4">{t('experience.technipfmc.location')}</p>
                <p className={`mb-4 leading-relaxed text-gray-700 dark:text-gray-300`}>{t('experience.technipfmc.description')}</p>
                <p className={`text-sm font-semibold text-gray-600 dark:text-gray-200`}>{t('experience.technipfmc.skills')}</p>
              </div>
            </div>
          </div>
        </section>

        {/* Education Section (Type 1) */}
        <section id="education" className={`py-16 md:py-24 bg-gray-50 text-light-text dark:bg-brand-purple dark:text-dark-text`}>
          <div className="container mx-auto px-6">
            <h2 className="text-3xl md:text-4xl font-bold text-center mb-12 flex items-center justify-center">
              <AcademicCapIcon className="h-8 w-8 mr-3 text-accent-teal" /> {t('education.title')}
            </h2>
            <div className="grid md:grid-cols-2 gap-8 max-w-4xl mx-auto">
              <div className="bg-white dark:bg-brand-purple-light p-6 rounded-xl shadow-lg card-hover">
                <h3 className="text-xl font-bold mb-1">{t('education.iff.institution')}</h3>
                <p className="text-md font-semibold text-accent-blue dark:text-accent-magenta mb-1">{t('education.iff.degree')}</p>
                <p className="text-sm text-gray-500 dark:text-gray-400 mb-2">{t('education.iff.period')}</p>
                <p className="text-sm text-gray-700 dark:text-gray-300">{t('education.iff.skills')}</p>
              </div>
              <div className="bg-white dark:bg-brand-purple-light p-6 rounded-xl shadow-lg card-hover">
                <h3 className="text-xl font-bold mb-1">{t('education.cnsdl.institution')}</h3>
                <p className="text-md font-semibold text-accent-blue dark:text-accent-magenta mb-1">{t('education.cnsdl.degree')}</p>
                <p className="text-sm text-gray-500 dark:text-gray-400">{t('education.cnsdl.period')}</p>
              </div>
            </div>
          </div>
        </section>

        {/* Licenses & Certifications Section (Type 2) - Show More/Less with Framer Motion */}
        <section 
          id="certifications" 
          ref={certificationsSectionRef} // Atribuir a ref à seção
          className={`py-16 md:py-24 bg-light-bg text-light-text dark:bg-brand-purple-dark dark:text-dark-text`}
        >
          <div className="container mx-auto px-6">
            <h2 className={`text-3xl md:text-4xl font-bold text-center mb-12 flex items-center justify-center`}>
              <AcademicCapIcon className="h-8 w-8 mr-3 text-accent-teal" />
              {t('certifications.title')}
            </h2>
            
            <motion.div 
              layout // Anima mudanças de layout (como a adição/remoção de itens)
              className="grid md:grid-cols-2 gap-8 max-w-5xl mx-auto"
            >
              <AnimatePresence>
                {/* Usar allCertificatesData aqui */}
                {allCertificatesData.slice(0, showAllCertificates ? allCertificatesData.length : initialCertificatesToShow).map((cert, index) => (
                  <motion.div
                    key={cert.key}
                    custom={index} // Passa o índice para as variantes para delay escalonado
                    variants={cardVariants}
                    initial="hidden"
                    animate="visible"
                    exit="exit"
                    layout // Anima a posição do card se outros forem removidos/adicionados
                    className={`p-6 rounded-xl shadow-xl card-hover flex flex-col bg-white text-light-text dark:bg-brand-purple-light dark:text-dark-text`}
                  >
                    <div className="flex items-center mb-4">
                      <img src={cert.logo} alt={`${cert.issuer} logo`} className="h-10 w-10 mr-4 object-contain dark:bg-slate-300 dark:p-1 dark:rounded-sm" />
                      <div>
                        <h3 className="text-lg md:text-xl font-bold leading-tight">{cert.name}</h3>
                        <p className={`text-sm font-semibold text-gray-600 dark:text-gray-300`}>{cert.issuer}</p>
                      </div>
                    </div>
                    <p className={`text-xs text-gray-500 dark:text-gray-400 mb-1`}>{cert.date}</p>
                    <p className={`text-xs text-gray-500 dark:text-gray-400 mb-3`}>
                      ID: {cert.credentialId}
                    </p>
                    
                    <div className="mb-4 mt-auto">
                      <p className={`text-sm font-semibold mb-1 text-gray-700 dark:text-gray-200`}>Skills:</p>
                      <div className="flex flex-wrap gap-2">
                        {cert.skills.map(skill => (
                          <span key={skill} className="text-xs bg-accent-teal/20 text-accent-teal dark:bg-accent-blue/20 dark:text-accent-blue px-2 py-1 rounded-full">{skill}</span>
                        ))}
                      </div>
                    </div>
                    <a 
                      href={cert.credentialUrl} 
                      target="_blank" 
                      rel="noopener noreferrer" 
                      className="text-sm font-medium text-accent-magenta dark:text-accent-blue hover:underline self-start"
                    >
                      {t('certifications.showCredentialText')} <i className="bi bi-box-arrow-up-right ml-1"></i>
                    </a>
                  </motion.div>
                ))}
              </AnimatePresence>
            </motion.div>

            {/* Botão Mostrar Mais / Mostrar Menos */}
            {/* Usar allCertificatesData aqui */}
            {allCertificatesData.length > initialCertificatesToShow && (
              <div className="text-center mt-12">
                <motion.button
                  onClick={toggleShowAllCertificates}
                  className="btn-secondary"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  {showAllCertificates ? t('certifications.showLess') : t('certifications.showMore')}
                </motion.button>
              </div>
            )}
          </div>
        </section>

        {/* Projects Section (Type 1) */}
        <section id="projects" className={`py-16 md:py-24 bg-gray-50 text-light-text dark:bg-brand-purple dark:text-dark-text`}>
          <div className="container mx-auto px-6">
            <h2 className="text-3xl md:text-4xl font-bold text-center mb-4 flex items-center justify-center">
                <CodeBracketIcon className="h-8 w-8 mr-3 text-accent-magenta" /> {t('projects.title')}
            </h2>
            <p className="text-lg text-center max-w-xl mx-auto mb-12 text-gray-600 dark:text-gray-400">{t('projects.description')}</p>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              {projectsPlaceholder.map((project) => (
                <div key={project.id} className="bg-white dark:bg-brand-purple-light rounded-xl shadow-lg overflow-hidden card-hover-glow animate-fadeInUp" style={{animationDelay: `${project.id * 100}ms`}}>
                  <img src={project.image} alt={project.title} className="w-full h-48 object-cover"/>
                  <div className="p-6">
                    <h3 className="text-xl font-bold mb-2">{project.title}</h3>
                    <p className="text-sm text-gray-700 dark:text-gray-300 mb-3">{project.description}</p>
                    <div className="flex flex-wrap gap-2 mb-4">
                        {project.tech.map(tech => (
                            <span key={tech} className="text-xs bg-accent-teal/20 text-accent-teal dark:bg-accent-blue/20 dark:text-accent-blue px-2 py-1 rounded-full">{tech}</span>
                        ))}
                    </div>
                    <a href={project.link} className="text-accent-magenta dark:text-accent-blue hover:underline font-semibold">View Details &rarr;</a>
                  </div>
                </div>
              ))}
            </div>
             <div className="text-center mt-12">
                <a href={githubProfileUrl} target="_blank" rel="noopener noreferrer" className="btn-secondary">
                    See more on GitHub
                </a>
            </div>
          </div>
        </section>

        {/* Contact Section (Type 2) */}
        <section id="contact" className={`py-16 md:py-24 bg-light-bg text-light-text dark:bg-brand-purple-dark dark:text-dark-text`}>
          <div className="container mx-auto px-6 text-center">
            <h2 className={`text-3xl md:text-4xl font-bold mb-4 flex items-center justify-center`}>
                <ChatBubbleLeftRightIcon className="h-8 w-8 mr-3 text-accent-blue" /> {t('contact.title')}
            </h2>
            <p className={`text-lg max-w-xl mx-auto mb-8 ${!darkMode ? 'text-gray-300' : 'text-gray-600'}`}>{t('contact.description')}</p>
            <a href={linkedinProfileUrl} target="_blank" rel="noopener noreferrer" className="btn-primary">
              <LinkIcon className="h-5 w-5 inline-block mr-2" /> {t('contact.linkedin')}
            </a>
          </div>
        </section>

      </main>

      <footer className="py-8 text-center bg-brand-purple-dark text-gray-400">
        <div className="container mx-auto px-6">
          <p>{t('footer.text')}</p>
        </div>
      </footer>
    </div>
  );
}

export default function SuspendedApp() {
  return (
    <Suspense fallback={<div className="flex justify-center items-center min-h-screen text-xl">Loading translations...</div>}>
      <App />
    </Suspense>
  );
}
