import React, { useState, useEffect, Suspense, useRef } from 'react';
import { useTranslation } from 'react-i18next';
import { motion, AnimatePresence } from 'framer-motion';
import {
  LanguageIcon, BriefcaseIcon, AcademicCapIcon,
  SparklesIcon, CodeBracketIcon, LinkIcon, RocketLaunchIcon,
  ChatBubbleLeftRightIcon, ArrowDownTrayIcon, EnvelopeIcon,
  CpuChipIcon // Added CpuChipIcon
} from '@heroicons/react/24/outline';

// Placeholder for 3D elements or visual flourishes
const FloatingShape = ({ className }) => (
  <div className={`absolute w-12 h-12 md:w-16 md:h-16 rounded-full opacity-10 dark:opacity-20 animate-float ${className}`}></div>
);

function App() {
  const { t, i18n } = useTranslation();
  const [currentLanguage, setCurrentLanguage] = useState(i18n.language.split('-')[0]);

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
    setCurrentLanguage(lng);
  };

  useEffect(() => {
    // Forçar modo escuro permanentemente
    document.documentElement.classList.add('dark');
    // localStorage.removeItem('darkMode'); // Opcional: remover a chave antiga do localStorage
  }, []); // Executa apenas uma vez na montagem

  useEffect(() => {
    setCurrentLanguage(i18n.language.split('-')[0]);
  }, [i18n.language]);


  const [showAllCertificates, setShowAllCertificates] = useState(false);
  // const allCertificatesData = t('certifications.certificates', { returnObjects: true }) || []; // Esta linha foi substituída pela versão robusta abaixo
  const initialCertificatesToShow = 4;

  const certificationsSectionRef = useRef(null);

  const toggleShowAllCertificates = () => {
    if (showAllCertificates && certificationsSectionRef.current) {
      certificationsSectionRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
    setShowAllCertificates(!showAllCertificates);
  };

  // --- Framer Motion Variants ---
  const cardVariants = { // Para certificados
    hidden: { opacity: 0, y: 20, scale: 0.95 },
    visible: (i) => ({
      opacity: 1,
      y: 0,
      scale: 1,
      transition: { delay: i * 0.05, duration: 0.3, ease: "easeOut" }
    }),
    exit: { opacity: 0, y: -10, scale: 0.95, transition: { duration: 0.2, ease: "easeIn" } }
  };

  const sectionVariant = { // Para seções inteiras como Skills, Education
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.5, when: "beforeChildren", staggerChildren: 0.1 }
    }
  };

  const skillItemVariant = { // Para cards de skills individuais
    hidden: { opacity: 0, scale: 0.8, y: 10 },
    visible: { opacity: 1, scale: 1, y: 0, transition: { duration: 0.3, ease: "easeOut" } }
  };
  
  /* // educationCardVariant foi comentado na reversão anterior
  const educationCardVariant = { 
    hidden: { opacity: 0, y: 20, scale: 0.95 },
    visible: (i) => ({
      opacity: 1,
      y: 0,
      scale: 1,
      transition: { delay: i * 0.1, duration: 0.4, ease: "easeOut" }
    })
  };
  */

  const tagVariants = { // Para tags no About Me
    hidden: { opacity: 0, y: 10, scale: 0.9 },
    visible: (i) => ({
      opacity: 1,
      y: 0,
      scale: 1,
      transition: { delay: i * 0.1, duration: 0.3, ease: "easeOut" },
    }),
  };

  const experienceCardVariant = {
    hidden: { opacity: 0, x: -50, scale: 0.95 },
    visible: (i) => ({
      opacity: 1,
      x: 0,
      scale: 1,
      transition: {
        delay: i * 0.15,
        duration: 0.5,
        ease: [0.25, 0.1, 0.25, 1], // Smoother ease
      }
    })
  };

  const experienceContentVariant = {
    hidden: { opacity: 0, y: 10 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.4,
        ease: "easeOut"
      }
    }
  };

  const contactIconVariant = {
    hidden: { opacity: 0, y: 20, scale: 0.8 },
    visible: (i) => ({ // 'i' aqui é o 'custom' index passado
      opacity: 1,
      y: 0,
      scale: 1,
      transition: {
        delay: i * 0.15, // Staggered delay for each icon
        duration: 0.4,
        ease: "easeOut"
      }
    })
  };
  // --- Fim das Framer Motion Variants ---

  const profilePhotoUrl = "/images/profile.jpg";
  const technipFmcLogoUrl = "/images/technipfmc.svg"; 
  const linkedinProfileUrl = "https://www.linkedin.com/in/joaopedrolopesgoncalves/";
  const githubProfileUrl = "https://github.com/DevJpLg";
  const emailAddress = "joaopedro.lg@hotmail.com";
  const cvFileUrl = "/Joao_Pedro_Goncalves_CV.pdf";

  const techColorMap = {
    "JavaScript": { bgColor: "bg-yellow-300", textColor: "text-yellow-800" },
    "CSS": { bgColor: "bg-blue-500", textColor: "text-white" },
    "React": { bgColor: "bg-sky-400", textColor: "text-sky-900" },
    "Python": { bgColor: "bg-green-400", textColor: "text-green-900" },
    "VBA": { bgColor: "bg-purple-500", textColor: "text-white" },
    "Oracle DB": { bgColor: "bg-red-600", textColor: "text-white" },
    "MS Power Platform": { bgColor: "bg-indigo-500", textColor: "text-white" },
    "Java": { bgColor: "bg-orange-500", textColor: "text-white" },
    "C++": { bgColor: "bg-blue-700", textColor: "text-white" },
    "HTML5": { bgColor: "bg-red-500", textColor: "text-white" },
    "CSS3": { bgColor: "bg-sky-600", textColor: "text-white" },
    "Node.js": { bgColor: "bg-green-600", textColor: "text-white" },
    "SQL": { bgColor: "bg-teal-500", textColor: "text-white" },
    "Git": { bgColor: "bg-red-500", textColor: "text-white" },
    "VS Code": { bgColor: "bg-blue-500", textColor: "text-white" },
    "Power BI": { bgColor: "bg-yellow-400", textColor: "text-black" },
    "Power Apps": { bgColor: "bg-purple-600", textColor: "text-white" },
    "SharePoint": { bgColor: "bg-teal-600", textColor: "text-white" },
    "Excel": { bgColor: "bg-green-700", textColor: "text-white" },
  };
  
  // Data for the new Skills section
  // IMPORTANT: Place your SVG logo image files in the `public/images/logos/` directory.
  // The names must match the `logoPath` values below.
  // For example:
  // - public/images/logos/python.svg
  // - public/images/logos/java.svg
  // - public/images/logos/javascript.svg
  // - public/images/logos/react.svg
  // - public/images/logos/cplusplus.svg
  // - public/images/logos/html5.svg
  // - public/images/logos/css3.svg
  // - public/images/logos/nodejs.svg
  // - public/images/logos/sql.svg
  // - public/images/logos/oracle.svg
  // - public/images/logos/git.svg
  // - public/images/logos/vscode.svg
  // - public/images/logos/powerbi.svg
  // - public/images/logos/powerapps.svg
  // - public/images/logos/sharepoint.svg
  // - public/images/logos/excel.svg
  const skillsList = [
    { name: "Python", logoPath: "/images/logos/python.svg" },
    { name: "Java", logoPath: "/images/logos/java.svg" },
    { name: "JavaScript", logoPath: "/images/logos/javascript.svg" },
    { name: "React", logoPath: "/images/logos/react.svg" },
    { name: "C++", logoPath: "/images/logos/cplusplus.svg" },
    { name: "HTML5", logoPath: "/images/logos/html5.svg" },
    { name: "CSS3", logoPath: "/images/logos/css3.svg" },
    { name: "Node.js", logoPath: "/images/logos/nodejs.svg" },
    { name: "SQL", logoPath: "/images/logos/sql.svg" },
    { name: "Oracle DB", logoPath: "/images/logos/oracle.svg" },
    { name: "Git", logoPath: "/images/logos/git.svg" },
    { name: "VS Code", logoPath: "/images/logos/vscode.svg" },
    { name: "Power BI", logoPath: "/images/logos/powerbi.svg" },
    { name: "Power Apps", logoPath: "/images/logos/powerapps.svg" },
    { name: "SharePoint", logoPath: "/images/logos/sharepoint.svg" },
    { name: "Excel", logoPath: "/images/logos/excel.svg" },
    { name: "VBA", IconComponent: CodeBracketIcon }, // Using a Heroicon
    { name: "Project Management", IconComponent: BriefcaseIcon }, // Using a Heroicon
  ];

  // Data fetching with robust array checks
  const certificatesDataRaw = t('certifications.certificates', { returnObjects: true });
  const allCertificatesData = Array.isArray(certificatesDataRaw) ? certificatesDataRaw : [];

  // A linha 155 e 156 são as declarações corretas e únicas para educationItemsDataRaw e educationItems
  const educationItemsDataRaw = t('education.items', { returnObjects: true });
  const educationItems = Array.isArray(educationItemsDataRaw) ? educationItemsDataRaw : [];
  // Certifique-se de que não há outra 'const educationItems = ...' antes desta seção.

  const experienceJobsDataRaw = t('experience.jobs', { returnObjects: true });
  const experienceJobs = Array.isArray(experienceJobsDataRaw) ? experienceJobsDataRaw : [];
  
  const skillsCategoriesDataRaw = t('skills.categories', { returnObjects: true });
  const skillsCategories = Array.isArray(skillsCategoriesDataRaw) ? skillsCategoriesDataRaw : [];

  const aboutKeyTechTagsDataRaw = t('about.keyTechTags', { returnObjects: true });
  const aboutKeyTechTags = Array.isArray(aboutKeyTechTagsDataRaw) ? aboutKeyTechTagsDataRaw : [];

  const projectsPlaceholder = [
    {
      id: 1,
      name: "Project Alpha",
      description: "A full-stack web application for managing tasks and projects.",
      html_url: "#",
      language: "JavaScript",
      stargazers_count: 10,
      forks_count: 2,
      topics: ["react", "nodejs", "express", "mongodb"]
    },
    {
      id: 2,
      name: "Data Analyzer",
      description: "A Python tool for analyzing and visualizing large datasets.",
      html_url: "#",
      language: "Python",
      stargazers_count: 25,
      forks_count: 5,
      topics: ["python", "pandas", "matplotlib", "data-science"]
    },
    // Adicione mais placeholders se necessário
  ];
  // projectsData foi introduzido na última correção, mantendo-o.
  const projectsData = Array.isArray(projectsPlaceholder) ? projectsPlaceholder : [];


  // Smooth scroll for navigation links
  const handleNavClick = (e, targetId) => {
    e.preventDefault();
    const element = document.getElementById(targetId);
    if (element) {
      window.scrollTo({
        top: element.offsetTop - 64, // Ajuste para o tamanho do seu cabeçalho
        behavior: 'smooth'
      });
    }
  };

  // Constantes para os links de contato
  const GITHUB_URL = "https://github.com/DevJpLg";
  const LINKEDIN_URL = "https://www.linkedin.com/in/joaopedrolopesgoncalves/";
  const EMAIL_ADDRESS = "joaopedro.lg@hotmail.com";
  const PHONE_NUMBER = "+5522999999999"; // Substitua pelo seu número de telefone formatado para WhatsApp ou ligação

  return (
    <div className="min-h-screen flex flex-col relative overflow-x-hidden bg-brand-purple-dark text-dark-text">
      {/* Header / Navbar */}
      <header className="sticky top-0 z-50 shadow-lg bg-brand-purple-dark/80 backdrop-blur-md"> {/* Forçando bg do modo escuro */}
        <nav className="container mx-auto px-6 py-3 flex justify-between items-center">
          <a href="#hero" onClick={(e) => handleNavClick(e, 'hero')} className="text-2xl font-bold text-accent-blue"> {/* Forçando cor do modo escuro */}
            {t('hero.name').split(' ')[0]} <span className="text-accent-magenta">{t('hero.name').split(' ').slice(1).join(' ')}</span>
          </a>
          <div className="flex items-center space-x-3">
            {/* Language Switcher */}
            <div className="relative">
              <button onClick={() => changeLanguage(currentLanguage === 'en' ? 'pt' : 'en')} className="btn-icon" aria-label="Change language">
                <LanguageIcon className="h-6 w-6" />
                <span className="ml-1 uppercase text-xs font-semibold">{currentLanguage}</span>
              </button>
            </div>
            {/* Dark Mode Toggle Removido 
            <button onClick={toggleDarkMode} className="btn-icon" aria-label="Toggle dark mode">
              {darkMode ? <SunIcon className="h-6 w-6" /> : <MoonIcon className="h-6 w-6" />}
            </button>
            */}
          </div>
        </nav>
      </header>

      <main className="flex-grow">
        {/* Hero Section */}
        <section id="hero" className="relative py-20 md:py-32 text-center bg-gradient-to-br from-brand-purple-dark to-brand-purple flex flex-col items-center justify-center min-h-[calc(100vh-64px)] overflow-hidden px-4"> {/* Forçando classes do modo escuro */}
          <div className="absolute inset-0 opacity-20"> {/* Ajustada opacidade para modo escuro */}
            {/* Background shapes or patterns */}
          </div>
          <div className="container mx-auto z-10 animate-fadeInUp">
            <h1 className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl font-black mb-3 text-dark-text"> {/* Forçando cor do modo escuro */}
              {t('hero.greeting')} <span className="bg-clip-text text-transparent bg-gradient-accent">{t('hero.name')}</span>
            </h1>
            <p className="text-lg sm:text-xl md:text-2xl font-semibold mb-5 text-gray-300 max-w-3xl mx-auto">{t('hero.title')}</p> {/* Forçando cor do modo escuro */
            }
            <p className="text-md sm:text-lg md:text-xl max-w-2xl mx-auto mb-8 text-gray-400"> {/* Forçando cor do modo escuro */}
              {t('hero.subtitle')}
            </p>
            <div className="flex flex-wrap justify-center items-center gap-4">
                <a 
                    href={`mailto:${emailAddress}`} 
                    className="btn-primary inline-block"
                >
                  <EnvelopeIcon className="h-5 w-5 inline-block mr-2" /> {t('hero.contactButtonHero')}
                </a>
                {/* Botão "View My Work" original, agora comentado ou removido
                <a href={githubProfileUrl} target="_blank" rel="noopener noreferrer" className="btn-primary inline-block">
                  <CodeBracketIcon className="h-5 w-5 inline-block mr-2" /> {t('hero.ctaButton')}
                </a>
                */}
                <a 
                    href={cvFileUrl}
                    download
                    className="btn-secondary inline-block"
                >
                    <ArrowDownTrayIcon className="h-5 w-5 inline-block mr-2" /> {t('hero.downloadCV')}
                </a>
            </div>
          </div>
        </section>

        {/* About Me Section */}
        <section id="about" className={`py-16 md:py-20 bg-brand-purple text-dark-text overflow-hidden`}> {/* Forçando classes do modo escuro */}
          <div className="container mx-auto px-6">
            <h2 className="text-3xl md:text-4xl font-bold text-center mb-12 flex items-center justify-center">
              <i className="bi bi-person-circle mr-3 text-3xl md:text-4xl text-accent-magenta"></i> {t('about.title')}
            </h2>
            <div className="flex flex-col md:flex-row items-center md:items-start gap-8 md:gap-12 animate-fadeInUp">
              <div className="w-56 h-56 md:w-64 md:h-64 lg:w-80 lg:h-80 flex-shrink-0 rounded-full overflow-hidden shadow-2xl border-4 border-accent-blue transform transition-all duration-500 hover:scale-105"> {/* Forçando cor da borda do modo escuro */}
                <img 
                  src={profilePhotoUrl} 
                  alt={t('hero.name')} 
                  className="w-full h-full object-cover"
                />
              </div>
              <div className="text-center md:text-left flex-grow">
                <p className="text-2xl md:text-3xl font-semibold mb-4">{t('about.intro')}</p>
                <p className="mb-4 text-base md:text-lg leading-relaxed text-gray-300">{t('about.paragraph1')}</p> {/* Forçando cor do modo escuro */}
                <p className="mb-6 text-base md:text-lg leading-relaxed text-gray-300">{t('about.paragraph2')}</p> {/* Forçando cor do modo escuro */}
                
                {/* Key Technology Tags */}
                <div className="mb-6 text-center md:text-left">
                  <div className="flex flex-wrap justify-center md:justify-start gap-2 md:gap-3">
                    {aboutKeyTechTags.map((tagObj, index) => {
                      // As cores das tags já são definidas no techColorMap, que pode ser ajustado para modo escuro se necessário
                      const colors = techColorMap[tagObj.name] || { bgColor: "bg-gray-700", textColor: "text-gray-200" }; // Fallback ajustado para modo escuro
                      return (
                        <motion.span 
                          key={tagObj.name} 
                          custom={index}
                          variants={tagVariants}
                          initial="hidden"
                          animate="visible"
                          className={`px-4 py-1.5 text-xs font-semibold rounded-lg shadow-md
                                      ${colors.bgColor} ${colors.textColor}
                                      transform transition-all duration-300 hover:scale-110 hover:shadow-lg`}
                        >
                          {tagObj.name}
                        </motion.span>
                      );
                    })}
                  </div>
                </div>

                {/* Location and Connections */}
                <div className="space-y-2 text-sm md:text-base text-gray-400"> {/* Forçando cor do modo escuro */}
                  <a 
                    href={t('about.locationUrl')} 
                    target="_blank" 
                    rel="noopener noreferrer" 
                    className="flex items-center justify-center md:justify-start hover:text-accent-teal transition-colors" // Ajustado hover para modo escuro
                  >
                    <i className="bi bi-geo-alt-fill mr-2 text-accent-teal"></i> {t('about.location')}
                  </a>
                  <a 
                    href={t('about.connectionsUrl')} 
                    target="_blank" 
                    rel="noopener noreferrer" 
                    className="flex items-center justify-center md:justify-start hover:text-accent-teal transition-colors" // Ajustado hover para modo escuro
                  >
                    <i className="bi bi-people-fill mr-2 text-accent-teal"></i> {t('about.connections')}
                  </a>
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* Experience Section */}
        <motion.section 
          id="experience" 
          className={`py-12 md:py-16 bg-brand-purple text-dark-text`}
          variants={sectionVariant}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.05 }}
        >
          <div className="container mx-auto px-4 sm:px-6">
            <h2 className="text-3xl md:text-4xl font-bold text-center mb-12 md:mb-16 flex items-center justify-center">
              <BriefcaseIcon className="h-8 w-8 mr-3 text-accent-teal" /> {/* Forçando cor do modo escuro */}
              {t('experience.title')}
            </h2>
            <div className="max-w-3xl mx-auto space-y-10">
              {experienceJobs.map((job, index) => (
                <motion.div 
                  key={job.key || index}
                  custom={index}
                  variants={experienceCardVariant}
                  className="bg-brand-purple-light rounded-xl shadow-2xl overflow-hidden card-hover-effect" // Forçando classes do modo escuro
                >
                  <div className="p-6 md:p-8">
                    <motion.div variants={experienceContentVariant} className="flex flex-col sm:flex-row items-start mb-4">
                      {job.logo && (
                        <img 
                          src={job.logo} 
                          alt={`${job.company} Logo`} 
                          className="h-12 w-12 md:h-14 md:w-14 mr-5 mb-3 sm:mb-0 flex-shrink-0 bg-slate-200 p-1 rounded-md object-contain" // Adicionado bg para logo no modo escuro
                        />
                      )}
                      <div className="flex-grow">
                        <h3 className="text-xl md:text-2xl font-bold text-white leading-tight">{job.title}</h3> {/* Forçando cor do modo escuro */}
                        <p className="text-md font-semibold text-accent-blue">{job.company}</p> {/* Forçando cor do modo escuro */}
                        <div className="text-xs text-gray-400 mt-1 space-x-2"> {/* Forçando cor do modo escuro */}
                          <span><i className="bi bi-calendar-event mr-1"></i>{job.dates}</span>
                          <span><i className="bi bi-geo-alt-fill mr-1"></i>{job.location}</span>
                        </div>
                      </div>
                    </motion.div>

                    <motion.div variants={experienceContentVariant}>
                      <h4 className="text-sm font-semibold text-gray-300 mb-2 mt-4">Responsibilities:</h4> {/* Forçando cor do modo escuro */}
                      <ul className="space-y-2 text-sm text-gray-300"> {/* Forçando cor do modo escuro */}
                        {Array.isArray(job.responsibilities) && job.responsibilities.map((resp, i) => (
                          <li key={i} className="flex items-start">
                            <i className="bi bi-check-circle-fill text-accent-blue mt-1 mr-2 flex-shrink-0"></i> {/* Forçando cor do modo escuro */}
                            <span>{resp}</span>
                          </li>
                        ))}
                      </ul>
                    </motion.div>
                  </div>
                </motion.div>
              ))}
            </div>
          </div>
        </motion.section>

        {/* Skills Section - NEW */}
        <motion.section
          id="skills"
          className={`py-16 md:py-24 bg-brand-purple text-dark-text`} // Consistent with Experience/Projects section bg
          variants={sectionVariant}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.05 }}
        >
          <div className="container mx-auto px-6">
            <h2 className="text-3xl md:text-4xl font-bold text-center mb-12 md:mb-16 flex items-center justify-center">
              <CpuChipIcon className="h-8 w-8 mr-3 text-accent-magenta" />
              {t('skills.title', "My Tech Stack & Expertise")} {/* Updated title */}
            </h2>
            <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-6 md:gap-8 max-w-5xl mx-auto">
              {skillsList.map((skill, index) => (
                <motion.div
                  key={skill.name}
                  custom={index}
                  variants={skillItemVariant}
                  className="bg-brand-purple-light p-4 md:p-6 rounded-xl shadow-xl text-center card-hover-effect flex flex-col items-center justify-center aspect-square transform transition-all duration-300 hover:scale-105"
                >
                  {skill.logoPath && (
                    <img 
                      src={skill.logoPath} 
                      alt={`${skill.name} logo`} 
                      className="h-12 w-12 md:h-16 md:w-16 mb-3 object-contain"
                      onError={(e) => { e.target.style.display = 'none'; /* Hide if image fails to load */ }}
                    />
                  )}
                  {skill.IconComponent && (
                    <skill.IconComponent className="h-12 w-12 md:h-16 md:w-16 mb-3 text-accent-blue" />
                  )}
                   {/* Fallback icon if image fails or no logoPath/IconComponent */
                  }
                  {!skill.logoPath && !skill.IconComponent && (
                    <CpuChipIcon className="h-12 w-12 md:h-16 md:w-16 mb-3 text-gray-500" />
                  )}
                  {/* Conditional rendering for text if image fails */}
                  {skill.logoPath && (
                     <p className="text-sm md:text-base font-semibold text-gray-200 mt-2">{skill.name}</p>
                  )}
                  {!skill.logoPath && (
                     <p className="text-sm md:text-base font-semibold text-gray-200">{skill.name}</p>
                  )}
                </motion.div>
              ))}
            </div>
          </div>
        </motion.section>

        {/* Education Section (DEPOIS de Skills) */}
        <motion.section 
          id="education" 
          className={`py-16 md:py-24 bg-brand-purple-dark text-dark-text`}
          variants={sectionVariant} 
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.1 }}
        >
          <div className="container mx-auto px-6">
            <h2 className="text-3xl md:text-4xl font-bold text-center mb-12 flex items-center justify-center">
              <AcademicCapIcon className="h-8 w-8 mr-3 text-accent-teal" /> {/* Forçando cor do modo escuro */}
              {t('education.title')}
            </h2>
            <div className="grid md:grid-cols-2 gap-8 max-w-4xl mx-auto">
              {educationItems.map((edu, index) => (
                <motion.div 
                  key={edu.institution + index} 
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true, amount: 0.3 }}
                  transition={{ duration: 0.5, delay: index * 0.1 }}
                  className="p-6 bg-brand-purple-light rounded-xl shadow-lg flex flex-col h-full" // Forçando classes do modo escuro
                >
                  <h3 className="text-xl font-bold text-white">{edu.institution}</h3> {/* Forçando cor do modo escuro */}
                  <p className="text-md font-semibold text-accent-blue">{edu.degree}</p> {/* Forçando cor do modo escuro */}
                  <p className="text-sm text-gray-400 mt-auto">{edu.dates}</p> {/* Forçando cor do modo escuro */}
                </motion.div>
              ))}
            </div>
          </div>
        </motion.section>

        {/* Projects Section */}
        <section id="projects" className={`py-16 md:py-24 bg-brand-purple text-dark-text`}> {/* Forçando classes do modo escuro */}
          <div className="container mx-auto px-6">
            <h2 className="text-3xl md:text-4xl font-bold text-center mb-12 flex items-center justify-center">
              <CodeBracketIcon className="h-8 w-8 mr-3 text-accent-teal" /> {t('projects.title')}
            </h2>
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
              {projectsData.map((project, index) => ( 
                <motion.div 
                  key={project.id || index} 
                  custom={index}
                  variants={cardVariants}
                  initial="hidden"
                  whileInView="visible"
                  viewport={{ once: true, amount: 0.2 }}
                  className="bg-brand-purple-light p-6 rounded-xl shadow-xl card-hover-glow flex flex-col" // Forçando classes do modo escuro
                >
                  <h3 className="text-xl font-bold mb-2 text-white">{project.name}</h3> {/* Forçando cor do modo escuro */}
                  <p className="text-sm text-gray-300 mb-3 flex-grow">{project.description}</p> {/* Forçando cor do modo escuro */}
                  {project.language && (
                    <p className="text-xs font-semibold text-accent-blue mb-1"> {/* Forçando cor do modo escuro */
                    }
                      Language: {project.language}
                    </p>
                  )}
                  <div className="flex items-center text-xs text-gray-400 mb-3"> {/* Forçando cor do modo escuro */}
                    <i className="bi bi-star-fill mr-1 text-yellow-400"></i> {project.stargazers_count}
                    <i className="bi bi-git mr-1 ml-3 text-gray-300"></i> {project.forks_count} {/* Forçando cor do modo escuro */}
                  </div>
                  {Array.isArray(project.topics) && project.topics.length > 0 && (
                    <div className="mb-3">
                      {project.topics.slice(0, 3).map(topic => (
                        <span key={topic} className="inline-block bg-accent-blue/20 text-accent-blue text-xs px-2 py-0.5 rounded-full mr-1 mb-1"> {/* Forçando classes do modo escuro */}
                          {topic}
                        </span>
                      ))}
                    </div>
                  )}
                  <a 
                    href={project.html_url} 
                    target="_blank" 
                    rel="noopener noreferrer" 
                    className="text-sm font-medium text-accent-teal hover:underline mt-auto self-start" // Forçando cor do modo escuro
                  >
                    {t('projects.viewProject')} <i className="bi bi-box-arrow-up-right ml-1"></i>
                  </a>
                </motion.div>
              ))}
            </div>
            <div className="text-center mt-12">
                <a href={githubProfileUrl} target="_blank" rel="noopener noreferrer" className="btn-secondary"> {/* btn-secondary pode precisar de ajuste para modo escuro */}
                    {t('projects.seeMore')} <i className="bi bi-github ml-2"></i>
                </a>
            </div>
          </div>
        </section>

        {/* Licenses & Certifications Section */}
        <section id="certifications" ref={certificationsSectionRef} className={`py-16 md:py-24 bg-brand-purple-dark text-dark-text`}> {/* Forçando classes do modo escuro */}
          <div className="container mx-auto px-6">
            <h2 className={`text-3xl md:text-4xl font-bold text-center mb-12 flex items-center justify-center`}>
              <AcademicCapIcon className="h-8 w-8 mr-3 text-accent-teal" />
              {t('certifications.title')}
            </h2>
            
            <motion.div 
              layout 
              className="grid md:grid-cols-2 gap-8 max-w-5xl mx-auto"
            >
              <AnimatePresence>
                {allCertificatesData.slice(0, showAllCertificates ? allCertificatesData.length : initialCertificatesToShow).map((cert, index) => (
                  <motion.div
                    key={cert.key}
                    custom={index} 
                    variants={cardVariants}
                    initial="hidden"
                    animate="visible"
                    exit="exit"
                    layout 
                    className={`p-6 rounded-xl shadow-xl card-hover flex flex-col bg-brand-purple-light text-dark-text`} // Forçando classes do modo escuro
                  >
                    <div className="flex items-center mb-4">
                      <img src={cert.logo} alt={`${cert.issuer} logo`} className="h-10 w-10 mr-4 object-contain bg-slate-300 p-1 rounded-sm" /> {/* Adicionado bg para logo */}
                      <div>
                        <h3 className="text-lg md:text-xl font-bold leading-tight">{cert.name}</h3>
                        <p className={`text-sm font-semibold text-gray-300`}>{cert.issuer}</p> {/* Forçando cor do modo escuro */}
                      </div>
                    </div>
                    <p className={`text-xs text-gray-400 mb-1`}>{cert.date}</p> {/* Forçando cor do modo escuro */}
                    <p className={`text-xs text-gray-400 mb-3`}> {/* Forçando cor do modo escuro */}
                      ID: {cert.credentialId}
                    </p>
                    
                    <div className="mb-4 mt-auto">
                      <p className={`text-sm font-semibold mb-1 text-gray-200`}>Skills:</p> {/* Forçando cor do modo escuro */}
                      <div className="flex flex-wrap gap-2">
                        {Array.isArray(cert.skills) && cert.skills.map(skill => (
                          <span key={skill} className="text-xs bg-accent-blue/20 text-accent-blue px-2 py-1 rounded-full">{skill}</span> /* Forçando classes do modo escuro */
                        ))}
                      </div>
                    </div>
                    <a 
                      href={cert.credentialUrl || '#'}
                      target="_blank" 
                      rel="noopener noreferrer" 
                      className="text-sm font-medium text-accent-blue hover:underline self-start" // Forçando cor do modo escuro
                    >
                      {t('certifications.showCredentialText')} <i className="bi bi-box-arrow-up-right ml-1"></i>
                    </a>
                  </motion.div>
                ))}
              </AnimatePresence>
            </motion.div>

            {allCertificatesData.length > initialCertificatesToShow && (
              <div className="text-center mt-12">
                <motion.button
                  onClick={toggleShowAllCertificates}
                  className="btn-secondary" // btn-secondary pode precisar de ajuste para modo escuro
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  {showAllCertificates ? t('certifications.showLess') : t('certifications.showMore')}
                </motion.button>
              </div>
            )}
          </div>
        </section>

        {/* Contact Icons Section (Replaces Get In Touch Form) */}
        <section id="contact" className={`py-16 md:py-24 bg-brand-purple text-dark-text`}>
          <div className="container mx-auto px-6 text-center">
            <h2 className={`text-3xl md:text-4xl font-bold mb-12 flex items-center justify-center`}>
              <ChatBubbleLeftRightIcon className="h-8 w-8 mr-3 text-accent-magenta" /> 
              {t('contact.title')}
            </h2>
            <div className="flex flex-wrap justify-center items-start gap-6 md:gap-10">
              {[ 
                { key: 'linkedin', href: LINKEDIN_URL, label: 'LinkedIn', icon: 'bi bi-linkedin' },
                { key: 'github', href: GITHUB_URL, label: 'GitHub', icon: 'bi bi-github' },
                { key: 'email', href: `mailto:${EMAIL_ADDRESS}`, label: 'Email', icon: 'bi bi-envelope-fill' },
                // WhatsApp icon removed
              ].map((contactItem, idx) => ( 
                <motion.a 
                  key={contactItem.key} 
                  href={contactItem.href} 
                  target={contactItem.key === 'email' ? '_self' : '_blank'} 
                  rel="noopener noreferrer" 
                  aria-label={contactItem.label} 
                  className="flex flex-col items-center text-gray-300 hover:text-accent-blue transition-colors duration-300 group"
                  custom={idx} 
                  variants={contactIconVariant}
                  initial="hidden"
                  whileInView="visible"
                  viewport={{ once: true, amount: 0.3 }}
                  whileHover={{ scale: 1.1, y: -5 }}
                  whileTap={{ scale: 0.95 }}
                >
                  <i className={`${contactItem.icon} text-5xl md:text-6xl transition-transform duration-300 group-hover:scale-110`}></i>
                  {/* Texto abaixo do ícone removido
                  <p className="text-xs mt-2">{contactItem.label}</p> 
                  */}
                </motion.a>
              ))}
            </div>
          </div>
        </section>
      </main>

      <footer className="py-8 text-center bg-brand-purple-dark text-gray-400">
        <div className="container mx-auto px-6">
          {/* Ícones de redes sociais removidos do footer */}
          {/* 
          <div className="flex justify-center space-x-6 mb-4">
            // ... (ícones removidos) ...
          </div>
          */}
          <p className="text-sm">&copy; {new Date().getFullYear()} {t('footer.ownerName')}. {t('footer.rights')}</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
